 64‚Üí      }
 65‚Üí    }
 66‚Üí  }
 67‚Üí`;
 68‚Üí
 69‚Üí// Helper function to get current user's role
 70‚Üíasync function getCurrentUserRole(userId: string): Promise<UserRole | "developer"> {
 71‚Üí  try {
 72‚Üí    const client = await clerkClient();
 73‚Üí    const user = await client.users.getUser(userId);
 74‚Üí    return (user.publicMetadata?.role as UserRole | "developer") || "viewer";
 75‚Üí  } catch (error) {
 76‚Üí    console.error("Error getting user role:", error);
 77‚Üí    return "viewer";
 78‚Üí  }
 79‚Üí}
 80‚Üí
 81‚Üí// Helper function to check if UUID format
 82‚Üífunction isUUID(str: string): boolean {
 83‚Üí  const uuidRegex =
 84‚Üí    /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
 85‚Üí  return uuidRegex.test(str);
 86‚Üí}
 87‚Üí
 88‚Üí// GET /api/users/[id] - Get specific user details
 89‚Üíexport async function GET(
 90‚Üí  request: NextRequest,
 91‚Üí  { params }: { params: Promise<{ id: string }> }
 92‚Üí) {
 93‚Üí  try {
 94‚Üí    const { userId } = await auth();
 95‚Üí
 96‚Üí    if (!userId) {
 97‚Üí      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 98‚Üí    }
 99‚Üí
100‚Üí    const { id: targetId } = await params;
101‚Üí    const currentUserRole = await getCurrentUserRole(userId);
102‚Üí    const permissions = getUserPermissions(currentUserRole);
103‚Üí
104‚Üí    // Users can view their own profile, admins/managers can view others
105‚Üí    const isSelf = targetId === userId;
106‚Üí    if (!permissions.canManageUsers && !isSelf) {
107‚Üí      return NextResponse.json(
108‚Üí        { error: "Insufficient permissions to view user details" },
109‚Üí        { status: 403 }
110‚Üí      );
111‚Üí    }
112‚Üí
113‚Üí    console.log(`üë§ Fetching user details for: ${targetId}`);
114‚Üí
115‚Üí    let userData;
116‚Üí    let errors;
117‚Üí
118‚Üí    // Determine if ID is UUID (database ID) or Clerk ID
119‚Üí    if (isUUID(targetId)) {
120‚Üí      // Database ID
121‚Üí      const result = await adminApolloClient.query({
122‚Üí        query: GET_USER_BY_ID,
123‚Üí        variables: { id: targetId },
124‚Üí        fetchPolicy: "network-only",
125‚Üí        errorPolicy: "all",
126‚Üí      });
127‚Üí      userData = result.data?.users_by_pk;
128‚Üí      errors = result.errors;
129‚Üí    } else {
130‚Üí      // Clerk ID
131‚Üí      const result = await adminApolloClient.query({
132‚Üí        query: GET_USER_BY_CLERK_ID,
133‚Üí        variables: { clerkId: targetId },
