148‚Üí
149‚Üí    if (!userData) {
150‚Üí      return NextResponse.json({ error: "User not found" }, { status: 404 });
151‚Üí    }
152‚Üí
153‚Üí    // Get additional Clerk information
154‚Üí    const client = await clerkClient();
155‚Üí    let clerkUser;
156‚Üí    try {
157‚Üí      clerkUser = await client.users.getUser(userData.clerk_user_id);
158‚Üí    } catch (error) {
159‚Üí      console.warn("Could not fetch Clerk user details:", error);
160‚Üí    }
161‚Üí
162‚Üí    return NextResponse.json({
163‚Üí      success: true,
