171‚Üí      },
172‚Üí      permissions: {
173‚Üí        canEdit: permissions.canManageUsers || isSelf,
174‚Üí        canChangeRole: permissions.canManageUsers && !isSelf,
175‚Üí        canDelete:
176‚Üí          permissions.canManageUsers && !isSelf && userData.role !== "developer",
177‚Üí        currentUserRole,
178‚Üí      },
179‚Üí    });
180‚Üí  } catch (error) {
181‚Üí    console.error("‚ùå Error fetching user:", error);
182‚Üí    return NextResponse.json(
183‚Üí      {
184‚Üí        error: "Failed to fetch user",
