192‚Üí// PUT /api/users/[id] - Update user details
193‚Üíexport async function PUT(
194‚Üí  request: NextRequest,
195‚Üí  { params }: { params: Promise<{ id: string }> }
196‚Üí) {
197‚Üí  try {
198‚Üí    const { userId } = await auth();
199‚Üí
200‚Üí    if (!userId) {
201‚Üí      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
202‚Üí    }
203‚Üí
204‚Üí    const { id: targetId } = await params;
