245‚Üí        variables: { id: targetId },
246‚Üí        fetchPolicy: "network-only",
247‚Üí      });
248‚Üí      targetUser = result.data?.users_by_pk;
249‚Üí    } else {
250‚Üí      const result = await adminApolloClient.query({
251‚Üí        query: GET_USER_BY_CLERK_ID,
252‚Üí        variables: { clerkId: targetId },
253‚Üí        fetchPolicy: "network-only",
254‚Üí      });
255‚Üí      targetUser = result.data?.users?.[0];
256‚Üí    }
257‚Üí
258‚Üí    if (!targetUser) {
259‚Üí      return NextResponse.json({ error: "User not found" }, { status: 404 });
260‚Üí    }
261‚Üí
262‚Üí    // Update user in Clerk first
263‚Üí    const client = await clerkClient();
264‚Üí    const updateData: any = {};
265‚Üí
266‚Üí    if (name) {
267‚Üí      const nameParts = name.split(" ");
268‚Üí      updateData.firstName = nameParts[0];
269‚Üí      updateData.lastName = nameParts.slice(1).join(" ");
270‚Üí    }
271‚Üí
272‚Üí    if (role) {
273‚Üí      updateData.publicMetadata = {
274‚Üí        ...targetUser.publicMetadata,
275‚Üí        role,
276‚Üí        managerId,
277‚Üí        isStaff: role === "developer" || role === "manager",
278‚Üí        lastUpdatedBy: userId,
279‚Üí        lastUpdatedAt: new Date().toISOString(),
280‚Üí      };
281‚Üí    }
282‚Üí
283‚Üí    if (Object.keys(updateData).length > 0) {
284‚Üí      await client.users.updateUser(targetUser.clerk_user_id, updateData);
285‚Üí      console.log(`‚úÖ Updated Clerk user: ${targetUser.clerk_user_id}`);
