      return NextResponse.json({
        success: true,
        message: `Reset to original state complete`,
        deletedVersions: deletedVersions || 0,
        resetPayrolls: resetPayrolls || 0,
      });
    } catch (error) {
      await client.query("ROLLBACK");
      throw error;
    } finally {
      client.release();
