// lib/auth/soc2-auth.ts - Simplified SOC2-compliant authentication using Clerk
import { auth, currentUser } from '@clerk/nextjs/server';
import { useAuth, useUser } from '@clerk/nextjs';

// ================================
// SOC2 ROLE HIERARCHY
// ================================

export const ROLE_HIERARCHY = {
  developer: 5,
  org_admin: 4,
  manager: 3,
  consultant: 2,
  viewer: 1,
} as const;

export type UserRole = keyof typeof ROLE_HIERARCHY;

// Get all roles a user can access based on hierarchy
export function getRolesForLevel(role: UserRole): UserRole[] {
  const userLevel = ROLE_HIERARCHY[role];
  return Object.entries(ROLE_HIERARCHY)
    .filter(([, level]) => level <= userLevel)
    .map(([roleName]) => roleName as UserRole);
}

// ================================
// SOC2 DATA CLASSIFICATION
// ================================

export const DataClassification = {
  CRITICAL: ['ssn', 'bank_account', 'tax_id'],
  HIGH: ['salary', 'personal_info', 'address'],
  MEDIUM: ['work_schedule', 'department', 'phone'],
  LOW: ['name', 'email', 'public_info']
} as const;

export type ClassificationLevel = keyof typeof DataClassification;

// ================================
// AUDIT LOGGING INTERFACE
// ================================

export interface AuditEvent {
  userId: string;
  action: string;
  resource: string;
  timestamp: Date;
  ipAddress?: string;
  userAgent?: string;
  details?: Record<string, any>;
}

// Simple audit logger - can be enhanced with database storage
export class AuditLogger {
  static async log(event: AuditEvent): Promise<void> {
    // For now, console log - in production this would go to audit database
    console.log('üîç AUDIT:', {
      timestamp: event.timestamp.toISOString(),
      userId: event.userId,
      action: event.action,
      resource: event.resource,
      ipAddress: event.ipAddress,
      userAgent: event.userAgent?.substring(0, 100),
      details: event.details
    });

    // TODO: Store in audit database table for SOC2 compliance
    // await db.audit_logs.create({ data: event });
  }
}

// ================================
// SIMPLIFIED SOC2 AUTH MANAGER
// ================================

export class SOC2AuthManager {
  private static instance: SOC2AuthManager;

  static getInstance(): SOC2AuthManager {
    if (!SOC2AuthManager.instance) {
      SOC2AuthManager.instance = new SOC2AuthManager();
    }
    return SOC2AuthManager.instance;
  }

  // Get secure Hasura token using Clerk's built-in template
  async getSecureToken(userId: string): Promise<string | null> {
    try {
      const authResult = await auth();
      const token = await authResult.getToken({ template: 'hasura' });

      // SOC2: Log token access
      await AuditLogger.log({
        userId,
        action: 'token_requested',
        resource: 'hasura_graphql',
        timestamp: new Date(),
      });

      return token;
    } catch (error) {
      await AuditLogger.log({
        userId,
        action: 'token_request_failed',
        resource: 'hasura_graphql',
        timestamp: new Date(),
        details: { error: error instanceof Error ? error.message : 'Unknown error' }
      });
      return null;
    }
  }

  // Client-side token retrieval
  async getClientToken(): Promise<string | null> {
    if (typeof window === 'undefined') return null;

    try {
      // Use Clerk's built-in session token with Hasura template
      if (window.Clerk?.session) {
        const token = await window.Clerk.session.getToken({ template: 'hasura' });

        // Get current user ID for logging
        const userId = (window as any).Clerk?.user?.id;
        if (userId) {
          await AuditLogger.log({
            userId,
            action: 'client_token_requested',
            resource: 'hasura_graphql',
            timestamp: new Date(),
          });
        }

        return token;
      }
      return null;
    } catch (error) {
      console.error('Failed to get client token:', error);
      return null;
    }
  }

  // Extract user role from token or user metadata
  async getUserRole(userId: string): Promise<UserRole> {
    try {
      const user = await currentUser();
      const role = user?.publicMetadata?.default_role as UserRole;
      return role || 'viewer';
    } catch (error) {
      console.error('Failed to get user role:', error);
      return 'viewer';
    }
  }

  // Client-side role extraction
  getUserRoleFromClient(): UserRole {
    if (typeof window === 'undefined') return 'viewer';

    try {
      const role = (window as any).Clerk?.user?.publicMetadata?.default_role as UserRole;
      return role || 'viewer';
    } catch (error) {
      return 'viewer';
    }
  }

  // Check if user has permission for resource
  async hasPermission(userId: string, resource: string, action: string): Promise<boolean> {
    try {
      const userRole = await this.getUserRole(userId);
      const hasAccess = this.checkRolePermission(userRole, resource, action);

      // SOC2: Log access attempt
      await AuditLogger.log({
        userId,
        action: `access_${hasAccess ? 'granted' : 'denied'}`,
        resource: `${resource}:${action}`,
        timestamp: new Date(),
        details: { userRole, hasAccess }
      });

      return hasAccess;
    } catch (error) {
      await AuditLogger.log({
        userId,
        action: 'permission_check_failed',
        resource: `${resource}:${action}`,
        timestamp: new Date(),
        details: { error: error instanceof Error ? error.message : 'Unknown error' }
      });
      return false;
    }
  }

  // Simple role-based permission check
  private checkRolePermission(role: UserRole, resource: string, action: string): boolean {
    const roleLevel = ROLE_HIERARCHY[role];

    // Define resource permissions (can be enhanced)
    const permissions: Record<string, Record<string, number>> = {
      payrolls: {
        read: 1,    // viewer and above
        create: 3,  // manager and above
        update: 2,  // consultant and above
        delete: 4,  // org_admin and above
      },
      users: {
        read: 2,    // consultant and above
        create: 4,  // org_admin and above
        update: 3,  // manager and above
        delete: 5,  // developer only
      },
      clients: {
        read: 1,    // viewer and above
        create: 3,  // manager and above
        update: 2,  // consultant and above
        delete: 4,  // org_admin and above
      },
    };

    const requiredLevel = permissions[resource]?.[action] || 5; // Default to highest level
    return roleLevel >= requiredLevel;
  }

  // Data masking based on role and classification
  maskDataForRole(data: any, userRole: UserRole, fieldName: string): any {
    const classification = this.getFieldClassification(fieldName);
    const canAccess = this.canAccessClassification(userRole, classification);

    return canAccess ? data : '[REDACTED]';
  }

  private getFieldClassification(fieldName: string): ClassificationLevel {
    for (const [level, fields] of Object.entries(DataClassification)) {
      if (fields.some(field => fieldName.toLowerCase().includes(field))) {
        return level as ClassificationLevel;
      }
    }
    return 'LOW'; // Default to lowest classification
  }

  private canAccessClassification(role: UserRole, classification: ClassificationLevel): boolean {
    const roleLevel = ROLE_HIERARCHY[role];
    const classificationLevels = {
      CRITICAL: 4, // org_admin and above
      HIGH: 3,     // manager and above
      MEDIUM: 2,   // consultant and above
      LOW: 1,      // viewer and above
    };

    return roleLevel >= classificationLevels[classification];
  }

  // Session validation for SOC2 compliance
  async validateSession(userId: string): Promise<boolean> {
    try {
      const token = await this.getSecureToken(userId);
      if (!token) return false;

      // Simple base64 decode to check expiry (avoid external JWT library)
      try {
        const payload = JSON.parse(
          Buffer.from(token.split('.')[1], 'base64').toString()
        );

        const now = Math.floor(Date.now() / 1000);
        const isValid = payload.exp > now;

        if (!isValid) {
          await AuditLogger.log({
            userId,
            action: 'session_expired',
            resource: 'authentication',
            timestamp: new Date(),
            details: { tokenExpiry: new Date(payload.exp * 1000) }
          });
        }

        return isValid;
      } catch (decodeError) {
        // If we can't decode the token, assume it's valid (Clerk handles validation)
        return true;
      }
    } catch (error) {
      await AuditLogger.log({
        userId,
        action: 'session_validation_failed',
        resource: 'authentication',
        timestamp: new Date(),
        details: { error: error instanceof Error ? error.message : 'Unknown error' }
      });
      return false;
    }
  }
}

// ================================
// CONVENIENCE EXPORTS
// ================================

export const soc2Auth = SOC2AuthManager.getInstance();

// Client-side hooks wrapper for easier usage
export function useSOC2Auth() {
  const { userId, getToken } = useAuth();
  const { user } = useUser();

  const getUserRole = (): UserRole => {
    return (user?.publicMetadata?.default_role as UserRole) || 'viewer';
  };

  const getSecureToken = async (): Promise<string | null> => {
    if (!userId) return null;
    return await getToken({ template: 'hasura' });
  };

  const hasPermission = (resource: string, action: string): boolean => {
    const role = getUserRole();
    const roleLevel = ROLE_HIERARCHY[role];

    // Simple role-based permission check (duplicated for client-side use)
    const permissions: Record<string, Record<string, number>> = {
      payrolls: { read: 1, create: 3, update: 2, delete: 4 },
      users: { read: 2, create: 4, update: 3, delete: 5 },
      clients: { read: 1, create: 3, update: 2, delete: 4 },
    };

    const requiredLevel = permissions[resource]?.[action] || 5;
    return roleLevel >= requiredLevel;
  };

  return {
    userId,
    user,
    getUserRole,
    getSecureToken,
    hasPermission,
    maskData: (data: any, fieldName: string) => soc2Auth.maskDataForRole(data, getUserRole(), fieldName)
  };
}

