// lib/apollo/simple-client.ts - Simplified Apollo Client using Clerk's Hasura template
import {
  ApolloClient,
  InMemoryCache,
  createHttpLink,
  from,
  split,
} from "@apollo/client";
import { setContext } from "@apollo/client/link/context";
import { onError } from "@apollo/client/link/error";
import { GraphQLWsLink } from "@apollo/client/link/subscriptions";
import { createClient } from "graphql-ws";
import { getMainDefinition } from "@apollo/client/utilities";
// Simple client-side audit logging
const clientAuditLog = (event: { userId: string; action: string; resource: string; details?: any }) => {
  console.log('üîç APOLLO AUDIT:', {
    timestamp: new Date().toISOString(),
    ...event
  });
  // TODO: Send to audit API endpoint in production
};

// ================================
// CACHE CONFIGURATION
// ================================

const cache = new InMemoryCache({
  typePolicies: {
    Query: {
      fields: {
        users: {
          merge(_, incoming) {
            return incoming;
          },
          keyArgs: ["where", "order_by"],
        },
        clients: {
          merge(_, incoming) {
            return incoming;
          },
          keyArgs: ["where", "order_by"],
        },
        payrolls: {
          merge(_, incoming) {
            return incoming;
          },
          keyArgs: ["where", "order_by"],
        },
      },
    },
    User: {
      keyFields: ["id"],
    },
    Client: {
      keyFields: ["id"],
    },
    Payroll: {
      keyFields: ["id"],
    },
  },
});

// ================================
// HTTP LINK
// ================================

const httpLink = createHttpLink({
  uri: process.env.NEXT_PUBLIC_HASURA_GRAPHQL_URL,
  credentials: "same-origin",
});

// ================================
// SIMPLIFIED AUTH LINK
// ================================

const authLink = setContext(async (_, { headers }) => {
  // Only add auth header on client-side
  if (typeof window !== "undefined") {
    try {
      // Use Clerk's built-in token directly
      if (window.Clerk?.session) {
        const token = await window.Clerk.session.getToken({ template: 'hasura' });

        // Simple audit logging
        const userId = (window as any).Clerk?.user?.id;
        if (userId && token) {
          clientAuditLog({
            userId,
            action: 'token_requested',
            resource: 'apollo_client',
          });
        }

        return {
          headers: {
            ...headers,
            ...(token && { authorization: `Bearer ${token}` }),
          },
        };
      }

      return { headers };
    } catch (error) {
      console.warn("Failed to get auth token:", error);
      return { headers };
    }
  }

  return { headers };
});

// ================================
// SIMPLIFIED ERROR HANDLING
// ================================

const errorLink = onError(({ graphQLErrors, networkError, operation, forward }) => {
  if (graphQLErrors) {
    for (const error of graphQLErrors) {
      // Handle JWT expiry - let Clerk handle refresh automatically
      if (
        error.extensions?.code === "invalid-jwt" ||
        error.message.includes("JWTExpired") ||
        error.message.includes("Could not verify JWT")
      ) {
        console.log("üîÑ JWT expired, Clerk will handle refresh on next request");

        // Log the event for SOC2 compliance
        if (typeof window !== "undefined") {
          const userId = (window as any).Clerk?.user?.id;
          if (userId) {
            clientAuditLog({
              userId,
              action: "jwt_expired",
              resource: "graphql",
              details: { operation: operation.operationName, error: error.message }
            });
          }
        }
      }

      // Log other GraphQL errors
      console.error(`GraphQL error: ${error.message}`, {
        operation: operation.operationName,
        code: error.extensions?.code,
      });
    }
  }

  if (networkError) {
    console.error("Network error:", networkError);

    // Log 401 errors for SOC2 compliance
    if ("statusCode" in networkError && networkError.statusCode === 401) {
      if (typeof window !== "undefined") {
        const userId = (window as any).Clerk?.user?.id;
        if (userId) {
          clientAuditLog({
            userId,
            action: "unauthorized_request",
            resource: "graphql",
            details: { operation: operation.operationName, statusCode: 401 }
          });
        }
      }
    }
  }
});

// ================================
// WEBSOCKET LINK (for subscriptions)
// ================================

const wsLink =
  typeof window !== "undefined"
    ? new GraphQLWsLink(
        createClient({
          url: process.env.NEXT_PUBLIC_HASURA_GRAPHQL_URL?.replace("http", "ws") || "",
          connectionParams: async () => {
            // Use Clerk's built-in token directly for WebSocket
            if (window.Clerk?.session) {
              const token = await window.Clerk.session.getToken({ template: 'hasura' });
              return {
                headers: {
                  ...(token && { authorization: `Bearer ${token}` }),
                },
              };
            }
            return { headers: {} };
          },
          retryAttempts: 3,
          shouldRetry: () => true,
          connectionAckWaitTimeout: 10000,
        })
      )
    : null;

// ================================
// SPLIT LINK (HTTP vs WebSocket)
// ================================

const splitLink =
  typeof window !== "undefined" && wsLink
    ? split(
        ({ query }) => {
          const definition = getMainDefinition(query);
          return (
            definition.kind === "OperationDefinition" &&
            definition.operation === "subscription"
          );
        },
        wsLink,
        from([errorLink, authLink, httpLink])
      )
    : from([errorLink, authLink, httpLink]);

// ================================
// APOLLO CLIENT
// ================================

export const client = new ApolloClient({
  link: splitLink,
  cache,
  connectToDevTools: process.env.NODE_ENV === "development",
  defaultOptions: {
    query: {
      fetchPolicy: "network-only" as const, // Always fetch fresh data for security
      errorPolicy: "all" as const,
      notifyOnNetworkStatusChange: true,
    },
    watchQuery: {
      fetchPolicy: "network-only" as const,
      errorPolicy: "all" as const,
      notifyOnNetworkStatusChange: true,
    },
    mutate: {
      errorPolicy: "all" as const,
    },
  },
});

// ================================
// SERVER-SIDE CLIENT
// Note: For server-side operations, use the existing server-apollo-client.ts
// ================================

export default client;

// middleware.ts - Simplified route protection with SOC2 audit logging
import { clerkMiddleware, createRouteMatcher } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { AuditLogger } from "./lib/auth/soc2-auth";

// ================================
// ROUTE MATCHERS
// ================================

// Public routes that don't require authentication
const isPublicRoute = createRouteMatcher([
  "/",
  "/sign-in(.*)",
  "/accept-invitation(.*)",
  "/api/clerk-webhooks(.*)",
  // Test routes (development only)
  ...(process.env.NODE_ENV === "development" ? [
    "/api/simple-test",
    "/api/debug-post", 
    "/api/minimal-post-test",
    "/api/working-post-test",
    "/api/test-get-public",
    "/api/test-minimal",
    "/api/test-create",
    "/api/test-direct-auth"
  ] : []),
  "/_next(.*)",
  "/favicon.ico",
]);

// ================================
// ENHANCED MIDDLEWARE
// ================================

export default clerkMiddleware(async (auth, req) => {
  // Skip authentication for public routes
  if (isPublicRoute(req)) {
    return NextResponse.next();
  }

  // Protect all non-public routes
  const authResult = await auth.protect();

  // SOC2: Log protected route access
  if (authResult?.userId) {
    const isApiRoute = req.nextUrl.pathname.startsWith('/api');

    // Only log significant routes, not internal ones
    if (!req.nextUrl.pathname.includes('_next') && 
        !req.nextUrl.pathname.includes('favicon')) {

      AuditLogger.log({
        userId: authResult.userId,
        action: isApiRoute ? 'api_access' : 'page_access',
        resource: req.nextUrl.pathname,
        timestamp: new Date(),
        details: {
          method: req.method,
          userAgent: req.headers.get('user-agent')?.substring(0, 100),
          ipAddress: req.headers.get('x-forwarded-for') || 
                    req.headers.get('x-real-ip') || 
                    'unknown',
        }
      });
    }
  }

  return NextResponse.next();
});

// ================================
// MIDDLEWARE CONFIGURATION
// ================================

export const config = {
  matcher: [
    // Skip Next.js internals and all static files
    "/((?!_next|[^?]*\\.(?:html?|css|js(?!on)|jpe?g|webp|png|gif|svg|ttf|woff2?|ico|csv|docx?|xlsx?|zip|webmanifest)).*)",
    // Always run for API routes
    "/(api|trpc)(.*)",
  ],
};

