"use client";

import * as React from "react";
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  useReactTable,
} from "@tanstack/react-table";
import { useQuery, useMutation, useApolloClient } from "@apollo/client";
// Simple auth error check
const isAuthError = (error: any): boolean => {
  return error?.graphQLErrors?.some((err: any) =>
    err.extensions?.code === "invalid-jwt" ||
    err.extensions?.code === "access-denied" ||
    err.message.includes("JWTExpired")
  ) || false;
};
import {
  GET_STAFF_LIST,
  GET_ALL_USERS_LIST,
} from "@/graphql/queries/staff/getStaffList";
import { UPDATE_STAFF } from "@/graphql/mutations/staff/updateStaff";
import { DELETE_STAFF } from "@/graphql/mutations/staff/deleteStaff";
import { useUserRole } from "@/hooks/useUserRole";
import { useCurrentUser } from "@/hooks/useCurrentUser";
import { useAuth } from "@clerk/nextjs";
import { Button } from "@/components/ui/button";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectTrigger,
  SelectValue,
  SelectContent,
  SelectItem,
} from "@/components/ui/select";
import {
  Trash2,
  UserPlus,
  Edit,
  Eye,
  Mail,
  User,
  Search,
  Filter,
  RefreshCcw,
  Users,
  Pencil,
