 212‚Üí// Helper function to format payroll cycle information
 213‚Üíconst formatPayrollCycle = (payroll: any) => {
 214‚Üí  const cycleName = payroll.payroll_cycle?.name;
 215‚Üí  const dateTypeName = payroll.payroll_date_type?.name;
 216‚Üí  const dateValue = payroll.date_value;
 217‚Üí
 218‚Üí  if (!cycleName) return "Not configured";
 219‚Üí
 220‚Üí  // Create readable cycle name
 221‚Üí  let readableCycle = "";
 222‚Üí  switch (cycleName) {
 223‚Üí    case "weekly":
 224‚Üí      readableCycle = "Weekly";
 225‚Üí      break;
 226‚Üí    case "fortnightly":
 227‚Üí      readableCycle = "Fortnightly";
 228‚Üí      break;
 229‚Üí    case "bi_monthly":
 230‚Üí      readableCycle = "Bi-Monthly";
 231‚Üí      break;
 232‚Üí    case "monthly":
 233‚Üí      readableCycle = "Monthly";
 234‚Üí      break;
 235‚Üí    case "quarterly":
 236‚Üí      readableCycle = "Quarterly";
 237‚Üí      break;
 238‚Üí    default:
 239‚Üí      readableCycle = cycleName.charAt(0).toUpperCase() + cycleName.slice(1);
 240‚Üí  }
 241‚Üí
 242‚Üí  // Add date type specific information
 243‚Üí  if (dateTypeName) {
 244‚Üí    switch (dateTypeName) {
 245‚Üí      case "fixed_date":
 246‚Üí        if (
 247‚Üí          (cycleName === "monthly" ||
 248‚Üí            cycleName === "quarterly" ||
 249‚Üí            cycleName === "bi_monthly") &&
 250‚Üí          dateValue
 251‚Üí        ) {
 252‚Üí          return `${readableCycle} - ${dateValue}${getOrdinalSuffix(
 253‚Üí            dateValue
 254‚Üí          )}`;
 255‚Üí        }
 256‚Üí        break;
 257‚Üí      case "eom":
 258‚Üí        return `${readableCycle} - EOM`;
 259‚Üí      case "som":
 260‚Üí        return `${readableCycle} - SOM`;
 261‚Üí      case "week_a":
 262‚Üí        if (cycleName === "fortnightly" && dateValue) {
 263‚Üí          return `${readableCycle} - Week A - ${getDayName(dateValue)}`;
 264‚Üí        }
 265‚Üí        break;
 266‚Üí      case "week_b":
 267‚Üí        if (cycleName === "fortnightly" && dateValue) {
 268‚Üí          return `${readableCycle} - Week B - ${getDayName(dateValue)}`;
 269‚Üí        }
 270‚Üí        break;
 271‚Üí      case "dow":
 272‚Üí        if (
 273‚Üí          (cycleName === "weekly" || cycleName === "fortnightly") &&
 274‚Üí          dateValue
 275‚Üí        ) {
 276‚Üí          return `${readableCycle} - ${getDayName(dateValue)}`;
 277‚Üí        }
 278‚Üí        break;
 279‚Üí    }
 280‚Üí  }
 281‚Üí
 282‚Üí  return readableCycle;
 283‚Üí};
 284‚Üí
 285‚Üí// Helper function to get ordinal suffix
 286‚Üíconst getOrdinalSuffix = (num: number) => {
 287‚Üí  const j = num % 10;
 288‚Üí  const k = num % 100;
 289‚Üí  if (j === 1 && k !== 11) return "st";
 290‚Üí  if (j === 2 && k !== 12) return "nd";
 291‚Üí  if (j === 3 && k !== 13) return "rd";
 292‚Üí  return "th";
 293‚Üí};
 294‚Üí
 295‚Üí// Helper function to get day name from number
 296‚Üíconst getDayName = (dayNumber: number) => {
 297‚Üí  const days = [
 298‚Üí    "Sunday",
 299‚Üí    "Monday",
 300‚Üí    "Tuesday",
 301‚Üí    "Wednesday",
 302‚Üí    "Thursday",
 303‚Üí    "Friday",
 304‚Üí    "Saturday",
 305‚Üí  ];
 306‚Üí  // Handle 1-7 range (Monday = 1) and 0-6 range (Sunday = 0)
 307‚Üí  if (dayNumber >= 1 && dayNumber <= 7) {
 308‚Üí    return days[dayNumber === 7 ? 0 : dayNumber]; // Convert 7 to 0 for Sunday
 309‚Üí  }
 310‚Üí  if (dayNumber >= 0 && dayNumber <= 6) {
 311‚Üí    return days[dayNumber];
 312‚Üí  }
 313‚Üí  return "Unknown";
 314‚Üí};
