 316‚Üí// Custom MultiSelect Component
 317‚Üíinterface MultiSelectProps {
 318‚Üí  options: Array<{ value: string; label: string }>;
 319‚Üí  selected: string[];
 320‚Üí  onSelectionChange: (selected: string[]) => void;
 321‚Üí  placeholder: string;
 322‚Üí  label?: string;
 323‚Üí}
 324‚Üí
 325‚Üífunction MultiSelect({
 326‚Üí  options,
 327‚Üí  selected,
 328‚Üí  onSelectionChange,
 329‚Üí  placeholder,
 330‚Üí  label,
 331‚Üí}: MultiSelectProps) {
 332‚Üí  const [open, setOpen] = useState(false);
 333‚Üí
 334‚Üí  const handleToggle = (value: string) => {
 335‚Üí    const newSelected = selected.includes(value)
 336‚Üí      ? selected.filter((item) => item !== value)
 337‚Üí      : [...selected, value];
 338‚Üí    onSelectionChange(newSelected);
 339‚Üí  };
 340‚Üí
 341‚Üí  const selectedLabels = options
 342‚Üí    .filter((option) => selected.includes(option.value))
 343‚Üí    .map((option) => option.label);
 344‚Üí
 345‚Üí  const displayText =
 346‚Üí    selectedLabels.length > 0
 347‚Üí      ? selectedLabels.length === 1
 348‚Üí        ? selectedLabels[0]
 349‚Üí        : `${selectedLabels.length} selected`
 350‚Üí      : placeholder;
 351‚Üí
 352‚Üí  return (
 353‚Üí    <Popover open={open} onOpenChange={setOpen}>
 354‚Üí      <PopoverTrigger asChild>
 355‚Üí        <Button
 356‚Üí          variant="outline"
 357‚Üí          role="combobox"
 358‚Üí          aria-expanded={open}
 359‚Üí          className="w-full justify-between"
 360‚Üí        >
 361‚Üí          {displayText}
 362‚Üí          <ChevronDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
 363‚Üí        </Button>
 364‚Üí      </PopoverTrigger>
 365‚Üí      <PopoverContent className="w-full p-0">
 366‚Üí        <div className="max-h-60 overflow-auto">
 367‚Üí          {options.map((option) => (
 368‚Üí            <div
 369‚Üí              key={option.value}
 370‚Üí              className="flex items-center space-x-2 p-2 hover:bg-accent cursor-pointer"
 371‚Üí              onClick={() => handleToggle(option.value)}
 372‚Üí            >
 373‚Üí              <Checkbox
 374‚Üí                checked={selected.includes(option.value)}
 375‚Üí                onChange={() => handleToggle(option.value)}
 376‚Üí              />
 377‚Üí              <span className="text-sm">{option.label}</span>
 378‚Üí            </div>
 379‚Üí          ))}
 380‚Üí        </div>
 381‚Üí      </PopoverContent>
 382‚Üí    </Popover>
 383‚Üí  );
 384‚Üí}
