 386‚Üíexport default function PayrollsPage() {
 387‚Üí  const [searchTerm, setSearchTerm] = useState("");
 388‚Üí  const [statusFilter, setStatusFilter] = useState<string[]>([]);
 389‚Üí  const [clientFilter, setClientFilter] = useState<string[]>([]);
 390‚Üí  const [consultantFilter, setConsultantFilter] = useState<string[]>([]);
 391‚Üí  const [payCycleFilter, setPayCycleFilter] = useState<string[]>([]);
 392‚Üí  const [dateTypeFilter, setDateTypeFilter] = useState<string[]>([]);
 393‚Üí  const [selectedPayrolls, setSelectedPayrolls] = useState<string[]>([]);
 394‚Üí  const [viewMode, setViewMode] = useState<ViewMode>("table");
 395‚Üí  const [showFilters, setShowFilters] = useState(false);
 396‚Üí
 397‚Üí  // Sorting state
 398‚Üí  const [sortField, setSortField] = useState<string>("nextEftDate");
 399‚Üí  const [sortDirection, setSortDirection] = useState<"asc" | "desc">("asc");
 400‚Üí
 401‚Üí  // Column visibility state
 402‚Üí  const [visibleColumns, setVisibleColumns] = useState<string[]>(
 403‚Üí    COLUMN_DEFINITIONS.filter((col) => col.defaultVisible).map((col) => col.key)
 404‚Üí  );
 405‚Üí
 406‚Üí  const {
 407‚Üí    isAdmin,
 408‚Üí    isManager,
 409‚Üí    isDeveloper,
 410‚Üí    isLoading: roleLoading,
 411‚Üí  } = useUserRole();
 412‚Üí
 413‚Üí  const { data, loading, error, refetch } = useQuery(GET_PAYROLLS, {
 414‚Üí    fetchPolicy: "cache-and-network",
 415‚Üí    errorPolicy: "all",
 416‚Üí  });
 417‚Üí
 418‚Üí  // Fallback query if the main query fails
 419‚Üí  const {
 420‚Üí    data: fallbackData,
 421‚Üí    loading: fallbackLoading,
 422‚Üí    error: fallbackError,
 423‚Üí    refetch: fallbackRefetch,
 424‚Üí  } = useQuery(GET_PAYROLLS_FALLBACK, {
 425‚Üí    skip: !error, // Only run fallback if main query fails
 426‚Üí    fetchPolicy: "cache-and-network",
 427‚Üí    errorPolicy: "all",
 428‚Üí  });
 429‚Üí
 430‚Üí  // Choose which data to use
 431‚Üí  const finalData = data || fallbackData;
 432‚Üí  const finalLoading = loading || (error && fallbackLoading);
 433‚Üí  const finalError = error && fallbackError ? fallbackError : null;
 434‚Üí  const finalRefetch = data ? refetch : fallbackRefetch;
 435‚Üí
 436‚Üí  const payrolls = finalData?.payrolls || [];
 437‚Üí
 438‚Üí  useEffect(() => {
 439‚Üí    if (roleLoading) {
 440‚Üí      const timeout = setTimeout(() => {
 441‚Üí        toast.info("Loading payrolls...", {
 442‚Üí          duration: 3000,
 443‚Üí          closeButton: true,
 444‚Üí        });
 445‚Üí      }, 2000);
 446‚Üí      return () => clearTimeout(timeout);
 447‚Üí    }
 448‚Üí  }, [roleLoading]);
 449‚Üí
 450‚Üí  if (roleLoading) return null;
 451‚Üí
 452‚Üí  if (finalLoading && !payrolls.length) {
 453‚Üí    return (
 454‚Üí      <div className="flex items-center justify-center min-h-[400px]">
 455‚Üí        <div className="text-center space-y-4">
 456‚Üí          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
 457‚Üí          <p className="text-gray-500">Loading payrolls...</p>
 458‚Üí        </div>
 459‚Üí      </div>
 460‚Üí    );
 461‚Üí  }
 462‚Üí
 463‚Üí  if (finalError && !payrolls.length) {
 464‚Üí    return (
 465‚Üí      <div className="flex items-center justify-center min-h-[400px]">
 466‚Üí        <div className="text-center space-y-4">
 467‚Üí          <AlertTriangle className="w-8 h-8 mx-auto text-destructive" />
 468‚Üí          <p className="text-destructive">
 469‚Üí            Error loading payrolls: {finalError.message}
 470‚Üí          </p>
 471‚Üí          <Button onClick={() => finalRefetch()} variant="outline">
 472‚Üí            <RefreshCw className="w-4 h-4 mr-2" />
 473‚Üí            Try Again
 474‚Üí          </Button>
 475‚Üí        </div>
 476‚Üí      </div>
 477‚Üí    );
 478‚Üí  }
 479‚Üí
 480‚Üí  // Transform payroll data
 481‚Üí  const transformedPayrolls = payrolls.map((payroll: any) => {
 482‚Üí    // Fix employee count - use the employee_count from the payroll directly
 483‚Üí    const employeeCount = payroll.employee_count || 0;
 484‚Üí
 485‚Üí    // Get next EFT date from payroll_dates
 486‚Üí    const getNextEftDate = (payrollDates: any[]) => {
 487‚Üí      if (!payrollDates || payrollDates.length === 0) return null;
 488‚Üí
 489‚Üí      const today = new Date();
 490‚Üí      const futureDates = payrollDates
 491‚Üí        .filter(
 492‚Üí          (date) =>
 493‚Üí            date.adjusted_eft_date && new Date(date.adjusted_eft_date) >= today
 494‚Üí        )
 495‚Üí        .sort(
 496‚Üí          (a, b) =>
 497‚Üí            new Date(a.adjusted_eft_date).getTime() -
 498‚Üí            new Date(b.adjusted_eft_date).getTime()
 499‚Üí        );
 500‚Üí
 501‚Üí      return futureDates.length > 0 ? futureDates[0].adjusted_eft_date : null;
 502‚Üí    };
 503‚Üí
 504‚Üí    return {
 505‚Üí      ...payroll,
 506‚Üí      employeeCount,
 507‚Üí      payrollCycle: formatPayrollCycle(payroll),
 508‚Üí      priority:
 509‚Üí        employeeCount > 50 ? "high" : employeeCount > 20 ? "medium" : "low",
 510‚Üí      progress: getStatusConfig(payroll.status || "Implementation").progress,
 511‚Üí      nextEftDate: getNextEftDate(payroll.payroll_dates),
 512‚Üí      lastUpdated: new Date(payroll.updated_at || payroll.created_at),
 513‚Üí      lastUpdatedBy: payroll.userByPrimaryConsultantUserId?.name || "System",
 514‚Üí    };
 515‚Üí  });
 516‚Üí
 517‚Üí  // Filter payrolls
 518‚Üí  const filteredPayrolls = transformedPayrolls.filter((payroll: any) => {
 519‚Üí    const matchesSearch =
 520‚Üí      !searchTerm ||
 521‚Üí      payroll.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
 522‚Üí      payroll.client?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
 523‚Üí      payroll.userByPrimaryConsultantUserId?.name
 524‚Üí        ?.toLowerCase()
 525‚Üí        .includes(searchTerm.toLowerCase()) ||
 526‚Üí      payroll.payrollCycle.toLowerCase().includes(searchTerm.toLowerCase());
 527‚Üí
 528‚Üí    const matchesStatus =
 529‚Üí      statusFilter.length === 0 || statusFilter.includes(payroll.status);
 530‚Üí
 531‚Üí    const matchesClient =
 532‚Üí      clientFilter.length === 0 || clientFilter.includes(payroll.client?.id);
 533‚Üí
 534‚Üí    const matchesConsultant =
 535‚Üí      consultantFilter.length === 0 ||
 536‚Üí      consultantFilter.includes(payroll.userByPrimaryConsultantUserId?.id);
 537‚Üí
 538‚Üí    const matchesPayCycle =
 539‚Üí      payCycleFilter.length === 0 ||
 540‚Üí      payCycleFilter.includes(payroll.payroll_cycle?.name);
 541‚Üí
 542‚Üí    const matchesDateType =
 543‚Üí      dateTypeFilter.length === 0 ||
 544‚Üí      dateTypeFilter.includes(payroll.payroll_date_type?.name);
 545‚Üí
 546‚Üí    return (
 547‚Üí      matchesSearch &&
 548‚Üí      matchesStatus &&
 549‚Üí      matchesClient &&
 550‚Üí      matchesConsultant &&
 551‚Üí      matchesPayCycle &&
 552‚Üí      matchesDateType
 553‚Üí    );
 554‚Üí  });
 555‚Üí
 556‚Üí  // Sort payrolls
 557‚Üí  const sortedPayrolls = [...filteredPayrolls].sort((a, b) => {
 558‚Üí    let aValue = a[sortField];
 559‚Üí    let bValue = b[sortField];
 560‚Üí
 561‚Üí    // Handle nested values
 562‚Üí    if (sortField === "client") {
 563‚Üí      aValue = a.client?.name || "";
 564‚Üí      bValue = b.client?.name || "";
 565‚Üí    } else if (sortField === "consultant") {
 566‚Üí      aValue = a.userByPrimaryConsultantUserId?.name || "";
 567‚Üí      bValue = b.userByPrimaryConsultantUserId?.name || "";
 568‚Üí    } else if (sortField === "employees") {
 569‚Üí      aValue = a.employeeCount;
 570‚Üí      bValue = b.employeeCount;
 571‚Üí    } else if (sortField === "lastUpdated") {
 572‚Üí      aValue = a.lastUpdated;
 573‚Üí      bValue = b.lastUpdated;
 574‚Üí    } else if (sortField === "nextEftDate") {
 575‚Üí      aValue = a.nextEftDate ? new Date(a.nextEftDate) : null;
 576‚Üí      bValue = b.nextEftDate ? new Date(b.nextEftDate) : null;
 577‚Üí    } else if (sortField === "payrollCycle") {
 578‚Üí      aValue = a.payrollCycle;
 579‚Üí      bValue = b.payrollCycle;
 580‚Üí    }
 581‚Üí
 582‚Üí    // Handle different data types
 583‚Üí    if (typeof aValue === "number" && typeof bValue === "number") {
 584‚Üí      return sortDirection === "asc" ? aValue - bValue : bValue - aValue;
 585‚Üí    }
 586‚Üí
 587‚Üí    if (aValue instanceof Date && bValue instanceof Date) {
 588‚Üí      return sortDirection === "asc"
 589‚Üí        ? aValue.getTime() - bValue.getTime()
 590‚Üí        : bValue.getTime() - aValue.getTime();
 591‚Üí    }
 592‚Üí
 593‚Üí    // Handle null dates (put nulls at the end)
 594‚Üí    if (sortField === "nextEftDate") {
 595‚Üí      if (aValue === null && bValue === null) return 0;
 596‚Üí      if (aValue === null) return 1;
 597‚Üí      if (bValue === null) return -1;
 598‚Üí      if (aValue instanceof Date && bValue instanceof Date) {
 599‚Üí        return sortDirection === "asc"
 600‚Üí          ? aValue.getTime() - bValue.getTime()
 601‚Üí          : bValue.getTime() - aValue.getTime();
 602‚Üí      }
 603‚Üí    }
 604‚Üí
 605‚Üí    // String comparison
 606‚Üí    const aStr = String(aValue || "").toLowerCase();
 607‚Üí    const bStr = String(bValue || "").toLowerCase();
 608‚Üí
 609‚Üí    if (sortDirection === "asc") {
 610‚Üí      return aStr.localeCompare(bStr);
 611‚Üí    } else {
 612‚Üí      return bStr.localeCompare(aStr);
 613‚Üí    }
 614‚Üí  });
 615‚Üí
