 119‚Üíexport default function AdvancedPayrollScheduler() {
 120‚Üí  const [currentDate, setCurrentDate] = useState(new Date());
 121‚Üí  const [viewPeriod, setViewPeriod] = useState<ViewPeriod>("month");
 122‚Üí  const [tableOrientation, setTableOrientation] = useState<TableOrientation>(
 123‚Üí    "consultants-as-columns"
 124‚Üí  );
 125‚Üí
 126‚Üí  // Add hydration-safe state
 127‚Üí  const [isClient, setIsClient] = useState(false);
 128‚Üí  
 129‚Üí  // Add custom CSS variables for dark mode support
 130‚Üí  React.useEffect(() => {
 131‚Üí    setIsClient(true);
 132‚Üí    const root = document.documentElement;
 133‚Üí    root.style.setProperty("--orange", "25 95% 53%"); // Orange color
 134‚Üí    root.style.setProperty("--orange-muted", "25 95% 53% / 0.4"); // Orange muted
 135‚Üí  }, []);
 136‚Üí
 137‚Üí  // Simplified state management
 138‚Üí  const [assignments, setAssignments] = useState<PayrollAssignment[]>([]);
 139‚Üí  const [originalAssignments, setOriginalAssignments] = useState<
 140‚Üí    PayrollAssignment[]
 141‚Üí  >([]);
 142‚Üí  const [isPreviewMode, setIsPreviewMode] = useState(false);
 143‚Üí  const [moveAsGroup] = useState(true);
 144‚Üí  const [showGhosts, setShowGhosts] = useState(true);
 145‚Üí
 146‚Üí  const [dragState, setDragState] = useState<DragState>({
 147‚Üí    isDragging: false,
 148‚Üí    draggedPayroll: null,
 149‚Üí    dragOverCell: null,
 150‚Üí  });
 151‚Üí
 152‚Üí  // Calculate date range
 153‚Üí  const dateRange = useMemo(() => {
 154‚Üí    let start: Date, end: Date;
 155‚Üí
 156‚Üí    switch (viewPeriod) {
 157‚Üí      case "week":
 158‚Üí        start = startOfWeek(currentDate);
 159‚Üí        end = endOfWeek(currentDate);
 160‚Üí        break;
 161‚Üí      case "fortnight":
 162‚Üí        start = startOfWeek(currentDate);
 163‚Üí        end = addDays(start, 13);
 164‚Üí        break;
 165‚Üí      case "month":
 166‚Üí        start = startOfMonth(currentDate);
 167‚Üí        end = endOfMonth(currentDate);
 168‚Üí        break;
 169‚Üí    }
 170‚Üí
 171‚Üí    return { start, end };
 172‚Üí  }, [currentDate, viewPeriod]);
 173‚Üí
 174‚Üí  // Generate dates array
 175‚Üí  const dates = useMemo(() => {
 176‚Üí    const dateArray: Date[] = [];
 177‚Üí    let current = dateRange.start;
 178‚Üí
 179‚Üí    while (current <= dateRange.end) {
 180‚Üí      const dateToAdd = new Date(current);
 181‚Üí      dateArray.push(dateToAdd);
 182‚Üí      current = addDays(current, 1);
 183‚Üí    }
 184‚Üí
 185‚Üí    return dateArray;
 186‚Üí  }, [dateRange]);
 187‚Üí
 188‚Üí  // Real GraphQL data fetching
 189‚Üí  const { data, loading, error, refetch } = useQuery(GetPayrollDataDocument, {
 190‚Üí    variables: {
 191‚Üí      start_date: format(dateRange.start, 'yyyy-MM-dd'),
 192‚Üí      end_date: format(dateRange.end, 'yyyy-MM-dd')
 193‚Üí    },
 194‚Üí    errorPolicy: 'all',
 195‚Üí    skip: !isClient // Skip query until client-side hydration is complete
 196‚Üí  });
 197‚Üí
 198‚Üí  const [updatePayrollConsultants, { loading: updating, error: updateError }] = useMutation(
 199‚Üí    UpdatePayrollConsultantsDocument
 200‚Üí  );
 201‚Üí
 202‚Üí  // Helper function to check if consultant is on leave
 203‚Üí  const isConsultantOnLeave = (consultant: any, date: Date): boolean => {
 204‚Üí    if (!consultant?.leaves) return false;
 205‚Üí
 206‚Üí    return consultant.leaves.some((leave: Leave) => {
 207‚Üí      const leaveStart = new Date(leave.start_date);
 208‚Üí      const leaveEnd = new Date(leave.end_date);
 209‚Üí      return (
 210‚Üí        leave.status === "approved" &&
 211‚Üí        isWithinInterval(date, { start: leaveStart, end: leaveEnd })
 212‚Üí      );
 213‚Üí    });
 214‚Üí  };
 215‚Üí
 216‚Üí  // Transform data into assignments
 217‚Üí  const transformData = (data: any): PayrollAssignment[] => {
 218‚Üí    if (!data?.payroll_dates) return [];
 219‚Üí
 220‚Üí    const assignmentList: PayrollAssignment[] = [];
 221‚Üí    const payrollsMap = new Map();
 222‚Üí
 223‚Üí    // Create payrolls map for quick lookup
 224‚Üí    data.payrolls?.forEach((payroll: any) => {
 225‚Üí      payrollsMap.set(payroll.id, payroll);
 226‚Üí    });
 227‚Üí
 228‚Üí    data.payroll_dates.forEach((dateInfo: any) => {
 229‚Üí      const payroll = dateInfo.payroll || payrollsMap.get(dateInfo.payroll_id);
 230‚Üí      if (!payroll) return;
 231‚Üí
 232‚Üí      const assignmentDate = new Date(dateInfo.adjusted_eft_date);
 233‚Üí      const primaryConsultant = payroll.userByPrimaryConsultantUserId;
 234‚Üí      const backupConsultant = payroll.userByBackupConsultantUserId;
 235‚Üí
 236‚Üí      let finalConsultantId = primaryConsultant?.id || "unassigned";
 237‚Üí      let finalConsultantName = primaryConsultant?.name || "Unassigned";
 238‚Üí      let isBackup = false;
 239‚Üí      let originalConsultantId: string | undefined;
 240‚Üí      let originalConsultantName: string | undefined;
 241‚Üí
 242‚Üí      // Check if primary consultant is on leave
 243‚Üí      if (
 244‚Üí        primaryConsultant &&
 245‚Üí        isConsultantOnLeave(primaryConsultant, assignmentDate)
 246‚Üí      ) {
 247‚Üí        if (backupConsultant) {
 248‚Üí          originalConsultantId = primaryConsultant.id;
 249‚Üí          originalConsultantName = primaryConsultant.name;
 250‚Üí          finalConsultantId = backupConsultant.id;
 251‚Üí          finalConsultantName = backupConsultant.name;
 252‚Üí          isBackup = true;
 253‚Üí        }
 254‚Üí      }
 255‚Üí
 256‚Üí      const assignment: PayrollAssignment = {
 257‚Üí        id: dateInfo.id,
 258‚Üí        payrollId: payroll.id,
 259‚Üí        payrollName: payroll.name,
 260‚Üí        clientName: payroll.client?.name || "Unknown Client",
 261‚Üí        originalEftDate: dateInfo.original_eft_date,
 262‚Üí        adjustedEftDate: dateInfo.adjusted_eft_date,
 263‚Üí        processingDate: dateInfo.processing_date,
 264‚Üí        employeeCount: payroll.employee_count || 0,
 265‚Üí        processingTime: payroll.processing_time || 1,
 266‚Üí        consultantId: finalConsultantId,
 267‚Üí        consultantName: finalConsultantName,
 268‚Üí        isBackup,
 269‚Üí        originalConsultantId,
 270‚Üí        originalConsultantName,
 271‚Üí      };
 272‚Üí
 273‚Üí      assignmentList.push(assignment);
 274‚Üí    });
 275‚Üí
 276‚Üí    return assignmentList;
 277‚Üí  };
 278‚Üí
 279‚Üí  // Initialize assignments when data loads
 280‚Üí  useEffect(() => {
 281‚Üí    if (data) {
 282‚Üí      const freshAssignments = transformData(data);
 283‚Üí      setAssignments(freshAssignments);
 284‚Üí      setOriginalAssignments([...freshAssignments]);
 285‚Üí      console.log("üìã Assignments set:", freshAssignments.length);
 286‚Üí    }
 287‚Üí  }, [data]);
 288‚Üí
 289‚Üí  // Extract consultants from data
 290‚Üí  const consultants = useMemo(() => {
 291‚Üí    if (!data?.payrolls) return [];
 292‚Üí
 293‚Üí    const consultantMap = new Map();
 294‚Üí
 295‚Üí    data.payrolls.forEach((payroll: any) => {
 296‚Üí      if (payroll.userByPrimaryConsultantUserId) {
 297‚Üí        consultantMap.set(
 298‚Üí          payroll.userByPrimaryConsultantUserId.id,
 299‚Üí          payroll.userByPrimaryConsultantUserId
 300‚Üí        );
 301‚Üí      }
 302‚Üí      if (payroll.userByBackupConsultantUserId) {
 303‚Üí        consultantMap.set(
 304‚Üí          payroll.userByBackupConsultantUserId.id,
 305‚Üí          payroll.userByBackupConsultantUserId
 306‚Üí        );
 307‚Üí      }
 308‚Üí    });
 309‚Üí
 310‚Üí    return Array.from(consultantMap.values());
 311‚Üí  }, [data]);
 312‚Üí
 313‚Üí  // Calculate consultant summaries
 314‚Üí  const consultantSummaries = useMemo(() => {
 315‚Üí    const summaries: ConsultantSummary[] = consultants.map((consultant) => {
 316‚Üí      const consultantAssignments = assignments.filter(
 317‚Üí        (a) => a.consultantId === consultant.id && !a.isGhost
 318‚Üí      );
 319‚Üí
 320‚Üí      const hasLeaveInPeriod = dates.some((date) =>
 321‚Üí        isConsultantOnLeave(consultant, date)
 322‚Üí      );
 323‚Üí
 324‚Üí      return {
 325‚Üí        id: consultant.id,
 326‚Üí        name: consultant.name,
 327‚Üí        totalPayrolls: consultantAssignments.length,
 328‚Üí        totalEmployees: consultantAssignments.reduce(
 329‚Üí          (sum, a) => sum + a.employeeCount,
 330‚Üí          0
 331‚Üí        ),
 332‚Üí        totalProcessingTime: consultantAssignments.reduce(
 333‚Üí          (sum, a) => sum + a.processingTime,
 334‚Üí          0
 335‚Üí        ),
 336‚Üí        isOnLeave: hasLeaveInPeriod,
 337‚Üí      };
 338‚Üí    });
 339‚Üí
 340‚Üí    return summaries;
 341‚Üí  }, [consultants, assignments, dates]);
 342‚Üí
 343‚Üí  const holidays = data?.holidays || [];
 344‚Üí
 345‚Üí  // Helper functions
 346‚Üí  const getHolidayForDate = (date: Date): Holiday | null => {
 347‚Üí    return (
 348‚Üí      holidays.find((holiday: Holiday) =>
 349‚Üí        isSameDay(new Date(holiday.date), date)
 350‚Üí      ) || null
 351‚Üí    );
 352‚Üí  };
 353‚Üí
 354‚Üí  const getProcessingTimeColor = (
 355‚Üí    processingTime: number,
 356‚Üí    isPreview = false,
 357‚Üí    isBackup = false,
 358‚Üí    isGhost = false,
 359‚Üí    isMoved = false
 360‚Üí  ) => {
 361‚Üí    if (isGhost) return "hsl(var(--muted) / 0.3)"; // Muted gray for ghosts
 362‚Üí
 363‚Üí    const maxTime = Math.max(...assignments.map((a) => a.processingTime));
 364‚Üí    const intensity = Math.min(processingTime / maxTime, 1);
 365‚Üí    const alpha = 0.2 + intensity * 0.6;
 366‚Üí
 367‚Üí    if (isPreview) return `hsl(var(--orange) / ${alpha})`; // Orange for preview
 368‚Üí    if (isBackup) return `hsl(var(--destructive) / ${alpha})`; // Red for backup
 369‚Üí    if (isMoved) return `hsl(var(--orange) / ${alpha})`; // Orange for moved assignments
 370‚Üí    return `hsl(var(--primary) / ${alpha})`; // Primary color for original positions
 371‚Üí  };
 372‚Üí
 373‚Üí  // Helper function to check if assignment has been moved
 374‚Üí  const isAssignmentMoved = (assignment: PayrollAssignment): boolean => {
 375‚Üí    if (assignment.isGhost) return false;
 376‚Üí
 377‚Üí    const original = originalAssignments.find(
 378‚Üí      (orig) => orig.id === assignment.id
 379‚Üí    );
 380‚Üí    if (!original) return false;
 381‚Üí
 382‚Üí    return (
 383‚Üí      assignment.consultantId !== original.consultantId ||
 384‚Üí      assignment.adjustedEftDate !== original.adjustedEftDate
 385‚Üí    );
 386‚Üí  };
 387‚Üí
 388‚Üí  // Helper function to get original consultant name for moved assignments
 389‚Üí  const getOriginalConsultantName = (
 390‚Üí    assignment: PayrollAssignment
 391‚Üí  ): string | null => {
 392‚Üí    if (assignment.isGhost) return null;
 393‚Üí
 394‚Üí    const original = originalAssignments.find(
 395‚Üí      (orig) => orig.id === assignment.id
 396‚Üí    );
 397‚Üí    if (!original) return null;
 398‚Üí
 399‚Üí    // Only return original consultant name if the assignment was actually moved
 400‚Üí    if (
 401‚Üí      assignment.consultantId !== original.consultantId ||
 402‚Üí      assignment.adjustedEftDate !== original.adjustedEftDate
 403‚Üí    ) {
 404‚Üí      return original.consultantName;
 405‚Üí    }
 406‚Üí
 407‚Üí    return null;
 408‚Üí  };
 409‚Üí
 410‚Üí  // Navigation functions
 411‚Üí  const navigatePrevious = () => {
 412‚Üí    // Clean up ghosts when navigating
 413‚Üí    if (isPreviewMode) {
 414‚Üí      setAssignments((prev) => removeGhosts(prev));
 415‚Üí    }
 416‚Üí
 417‚Üí    switch (viewPeriod) {
 418‚Üí      case "week":
 419‚Üí        setCurrentDate(subWeeks(currentDate, 1));
 420‚Üí        break;
 421‚Üí      case "fortnight":
 422‚Üí        setCurrentDate(subWeeks(currentDate, 2));
 423‚Üí        break;
 424‚Üí      case "month":
 425‚Üí        setCurrentDate(subMonths(currentDate, 1));
 426‚Üí        break;
 427‚Üí    }
 428‚Üí  };
 429‚Üí
 430‚Üí  const navigateNext = () => {
 431‚Üí    // Clean up ghosts when navigating
 432‚Üí    if (isPreviewMode) {
 433‚Üí      setAssignments((prev) => removeGhosts(prev));
 434‚Üí    }
 435‚Üí
 436‚Üí    switch (viewPeriod) {
 437‚Üí      case "week":
 438‚Üí        setCurrentDate(addWeeks(currentDate, 1));
 439‚Üí        break;
 440‚Üí      case "fortnight":
 441‚Üí        setCurrentDate(addWeeks(currentDate, 2));
 442‚Üí        break;
 443‚Üí      case "month":
 444‚Üí        setCurrentDate(addMonths(currentDate, 1));
 445‚Üí        break;
 446‚Üí    }
 447‚Üí  };
 448‚Üí
 449‚Üí  // Drag and drop handlers
 450‚Üí  const handleDragStart = (
 451‚Üí    e: React.DragEvent,
 452‚Üí    assignment: PayrollAssignment
 453‚Üí  ) => {
 454‚Üí    if (!isPreviewMode || loading || updating) {
 455‚Üí      e.preventDefault();
 456‚Üí      return;
 457‚Üí    }
 458‚Üí
 459‚Üí    setDragState({
 460‚Üí      isDragging: true,
 461‚Üí      draggedPayroll: assignment,
 462‚Üí      dragOverCell: null,
 463‚Üí    });
 464‚Üí    e.dataTransfer.effectAllowed = "move";
 465‚Üí  };
 466‚Üí
 467‚Üí  const handleDragEnd = () => {
 468‚Üí    setDragState({
 469‚Üí      isDragging: false,
 470‚Üí      draggedPayroll: null,
 471‚Üí      dragOverCell: null,
 472‚Üí    });
 473‚Üí  };
 474‚Üí
 475‚Üí  const handleDragOver = (
 476‚Üí    e: React.DragEvent,
 477‚Üí    consultantId: string,
 478‚Üí    date: string
 479‚Üí  ) => {
 480‚Üí    if (!isPreviewMode) return;
 481‚Üí    e.preventDefault();
 482‚Üí    setDragState((prev) => ({
 483‚Üí      ...prev,
 484‚Üí      dragOverCell: { consultantId, date },
 485‚Üí    }));
 486‚Üí  };
 487‚Üí
 488‚Üí  const handleDrop = (
 489‚Üí    e: React.DragEvent,
 490‚Üí    targetConsultantId: string,
 491‚Üí    targetDate: string
 492‚Üí  ) => {
 493‚Üí    e.preventDefault();
 494‚Üí    if (!isPreviewMode) return;
 495‚Üí
 496‚Üí    const { draggedPayroll } = dragState;
 497‚Üí    if (!draggedPayroll) return;
 498‚Üí
 499‚Üí    setAssignments((prev) => {
 500‚Üí      const newGhosts: PayrollAssignment[] = [];
 501‚Üí
 502‚Üí      if (moveAsGroup) {
 503‚Üí        // Move all assignments for this payroll
 504‚Üí        const updatedAssignments = prev
 505‚Üí          .filter((a) => {
 506‚Üí            // Remove existing ghosts for this payroll only
 507‚Üí            if (a.isGhost && a.payrollId === draggedPayroll.payrollId) {
 508‚Üí              return false; // Remove old ghosts for this payroll
 509‚Üí            }
 510‚Üí            return true; // Keep everything else
 511‚Üí          })
 512‚Üí          .map((a) => {
