 643‚Üí
 644‚Üí    // Only look at non-ghost assignments
 645‚Üí    const realAssignments = removeGhosts(assignments);
 646‚Üí
 647‚Üí    realAssignments.forEach((current) => {
 648‚Üí      const original = originalAssignments.find(
 649‚Üí        (orig) => orig.id === current.id
 650‚Üí      );
 651‚Üí      if (
 652‚Üí        original &&
 653‚Üí        (current.consultantId !== original.consultantId ||
 654‚Üí          current.adjustedEftDate !== original.adjustedEftDate)
 655‚Üí      ) {
 656‚Üí        const changeKey = current.payrollId;
 657‚Üí
 658‚Üí        if (!changesMap.has(changeKey)) {
 659‚Üí          changesMap.set(changeKey, {
 660‚Üí            payrollId: current.payrollId,
 661‚Üí            payrollName: current.payrollName,
 662‚Üí            fromConsultantId: original.consultantId,
