 665‚Üí            toConsultantName: current.consultantName,
 666‚Üí            affectedDates: [current.adjustedEftDate],
 667‚Üí          });
 668‚Üí        } else {
 669‚Üí          const existingChange = changesMap.get(changeKey)!;
 670‚Üí          if (!existingChange.affectedDates.includes(current.adjustedEftDate)) {
 671‚Üí            existingChange.affectedDates.push(current.adjustedEftDate);
 672‚Üí          }
 673‚Üí        }
 674‚Üí      }
 675‚Üí    });
 676‚Üí
 677‚Üí    return Array.from(changesMap.values());
 678‚Üí  }, [assignments, originalAssignments]);
