# User domain queries - simplified version

query GetUsers(
  $limit: Int = 50
  $offset: Int = 0
  $where: users_bool_exp = {}
  $order_by: [users_order_by!] = [{ created_at: desc }]
) {
  users(limit: $limit, offset: $offset, where: $where, order_by: $order_by) {
    ...UserFragment
    manager {
      id
      name
    }
  }
  users_aggregate(where: $where) {
    aggregate {
      count
    }
  }
}

query GetUserById($id: uuid!) {
  users_by_pk(id: $id) {
    ...UserWithManagerFragment
  }
}

query GetStaffMembers(
  $limit: Int = 50
  $where: users_bool_exp = { is_staff: { _eq: true } }
  $order_by: [users_order_by!] = [{ name: asc }]
) {
  users(limit: $limit, where: $where, order_by: $order_by) {
    ...UserFragment
    manager {
      id
      name
    }
  }
  users_aggregate(where: $where) {
    aggregate {
      count
    }
  }
}

