file.content:
# Payroll Queries

query GetPayrolls {
  payrolls(
    where: { superseded_date: { _is_null: true } }
    order_by: { updated_at: desc }
  ) {
    ...PayrollWithDates
  }
}

query GetPayrollById($id: uuid!) {
  payrolls_by_pk(id: $id) {
    ...PayrollWithDates
  }
}

query GetPayrollsByClient($clientId: uuid!) {
  payrolls(
    where: {
      client_id: { _eq: $clientId }
      superseded_date: { _is_null: true }
    }
    order_by: { updated_at: desc }
  ) {
    ...PayrollWithDates
  }
}

query GetPayrollsByMonth($startDate: date!, $endDate: date!) {
  payrolls(
    where: {
      superseded_date: { _is_null: true }
      payroll_dates: { adjusted_eft_date: { _gte: $startDate, _lte: $endDate } }
    }
    order_by: { updated_at: desc }
  ) {
    ...PayrollWithDates
  }
}

query GetPayrollDates($payrollId: uuid!) {
  payroll_dates(
    where: { payroll_id: { _eq: $payrollId } }
    order_by: { adjusted_eft_date: asc }
  ) {
    ...PayrollDate
  }
}

query GetPayrollFamilyDates($payrollId: uuid!) {
  payrolls(
    where: {
      _or: [
        { id: { _eq: $payrollId } }
        { parent_payroll_id: { _eq: $payrollId } }
      ]
    }
    order_by: { version_number: asc }
  ) {
    id
    version_number
    payroll_dates(order_by: { adjusted_eft_date: asc }) {
      ...PayrollDate
    }
  }
}

query GetPayrollsMissingDates {
  payrolls(
    where: { superseded_date: { _is_null: true }, _not: { payroll_dates: {} } }
  ) {
    id
    name
    client {
      name
    }
  }
}

query CheckPayrollVersion($id: uuid!) {
  payrolls_by_pk(id: $id) {
    id
    superseded_date
    version_number
  }
  latest: payrolls(
    where: {
      parent_payroll_id: { _eq: $id }
      superseded_date: { _is_null: true }
    }
    limit: 1
  ) {
    id
  }
}

# Scheduler query for payroll data with dates and holidays
query GetPayrollData($start_date: date!, $end_date: date!) {
  payrolls {
    id
    name
    employee_count
    processing_time
    status
    client {
      id
      name
    }
    userByPrimaryConsultantUserId {
      id
      name
      leaves {
        id
        start_date
        end_date
        leave_type
        reason
        status
      }
    }
    userByBackupConsultantUserId {
      id
      name
      leaves {
        id
        start_date
        end_date
        leave_type
        reason
        status
      }
    }
  }
  payroll_dates(
    where: { adjusted_eft_date: { _gte: $start_date, _lte: $end_date } }
  ) {
    id
    payroll_id
    original_eft_date
    adjusted_eft_date
    processing_date
    payroll {
      id
      name
      employee_count
      processing_time
      client {
        name
      }
      userByPrimaryConsultantUserId {
        id
        name
      }
      userByBackupConsultantUserId {
        id
        name
      }
    }
  }
  holidays(where: { date: { _gte: $start_date, _lte: $end_date } }) {
    id
    date
    local_name
    types
    region
    country_code
  }
}

