  1‚Üí// lib/middleware/rate-limiter.ts - Comprehensive rate limiting middleware
  2‚Üíimport { NextRequest, NextResponse } from 'next/server';
  3‚Üíimport { EnhancedRouteMonitor } from '../security/enhanced-route-monitor';
  4‚Üí
  5‚Üíexport interface RateLimitConfig {
  6‚Üí  requests: number;
  7‚Üí  window: number; // in milliseconds
  8‚Üí  message?: string;
  9‚Üí  skipIf?: (request: NextRequest) => boolean;
 10‚Üí}
 11‚Üí
 12‚Üíexport interface RateLimitResult {
 13‚Üí  success: boolean;
 14‚Üí  limit: number;
 15‚Üí  remaining: number;
 16‚Üí  resetTime: number;
 17‚Üí  retryAfter?: number;
 18‚Üí}
 19‚Üí
 20‚Üíexport class RateLimiter {
 21‚Üí  private static instance: RateLimiter;
 22‚Üí  private requestCounts = new Map<string, { count: number; resetTime: number }>();
 23‚Üí  private monitor = EnhancedRouteMonitor.getInstance();
 24‚Üí
 25‚Üí  // Comprehensive rate limits by route pattern
 26‚Üí  private readonly ROUTE_LIMITS: { [pattern: string]: RateLimitConfig } = {
 27‚Üí    // Authentication routes - strictest limits
 28‚Üí    '/api/auth/token': { requests: 10, window: 60000, message: 'Too many token requests' },
 29‚Üí    '/api/auth/hasura-claims': { requests: 5, window: 60000 },
 30‚Üí    '/api/auth/debug-token': { requests: 3, window: 60000 },
 31‚Üí
 32‚Üí    // User management - strict limits
 33‚Üí    '/api/users': { requests: 50, window: 60000 },
 34‚Üí    '/api/users/[id]': { requests: 30, window: 60000 },
 35‚Üí    '/api/users/update-profile': { requests: 10, window: 60000 },
 36‚Üí    '/api/update-user-role': { requests: 3, window: 60000, message: 'Role changes are strictly limited' },
 37‚Üí    
 38‚Üí    // Staff management - very strict
 39‚Üí    '/api/staff/create': { requests: 5, window: 300000 },
 40‚Üí    '/api/staff/delete': { requests: 3, window: 300000, message: 'Staff deletion is strictly limited' },
 41‚Üí    '/api/staff/update-role': { requests: 5, window: 60000 },
 42‚Üí    '/api/staff/invitation-status': { requests: 20, window: 60000 },
 43‚Üí
 44‚Üí    // Admin operations - extremely strict
 45‚Üí    '/api/admin/api-keys': { requests: 5, window: 300000, message: 'API key operations are strictly limited' },
 46‚Üí    '/api/audit/compliance-report': { requests: 3, window: 300000 },
 47‚Üí    '/api/audit/log': { requests: 10, window: 60000 },
 48‚Üí
 49‚Üí    // Payroll operations - moderate limits
 50‚Üí    '/api/payrolls': { requests: 100, window: 60000 },
 51‚Üí    '/api/payrolls/[id]': { requests: 50, window: 60000 },
 52‚Üí    '/api/payroll-dates/[payrollId]': { requests: 30, window: 60000 },
 53‚Üí    '/api/payroll-dates/generated': { requests: 20, window: 60000 },
 54‚Üí    '/api/commit-payroll-assignments': { requests: 10, window: 60000 },
 55‚Üí
 56‚Üí    // Sync operations - moderate limits
 57‚Üí    '/api/sync-current-user': { requests: 20, window: 60000 },
 58‚Üí    '/api/fix-oauth-user': { requests: 5, window: 60000 },
 59‚Üí
 60‚Üí    // AI/Chat operations - user-specific limits
 61‚Üí    '/api/chat': { requests: 20, window: 60000 },
 62‚Üí
 63‚Üí    // Developer tools - strict limits
 64‚Üí    '/api/developer/clean-single-payroll': { requests: 5, window: 300000 },
 65‚Üí    '/api/developer/regenerate-all-dates': { requests: 2, window: 300000 },
 66‚Üí    '/api/developer/reset-to-original': { requests: 3, window: 600000, message: 'Reset operations are extremely limited' },
 67‚Üí
 68‚Üí    // Cron operations - very strict
 69‚Üí    '/api/cron/generate-bulk-dates': { requests: 1, window: 300000 },
 70‚Üí    '/api/holidays/sync': { requests: 1, window: 3600000 }, // 1 per hour
 71‚Üí
 72‚Üí    // Read-only operations - lenient limits
 73‚Üí    '/api/check-role': { requests: 200, window: 60000 },
 74‚Üí    '/api/payrolls/schedule': { requests: 100, window: 60000 },
 75‚Üí
 76‚Üí    // Default fallback
 77‚Üí    'default': { requests: 50, window: 60000 }
 78‚Üí  };
 79‚Üí
 80‚Üí  private constructor() {
 81‚Üí    // Clean up expired entries every 5 minutes
 82‚Üí    setInterval(() => this.cleanup(), 5 * 60 * 1000);
 83‚Üí  }
 84‚Üí
 85‚Üí  static getInstance(): RateLimiter {
 86‚Üí    if (!RateLimiter.instance) {
 87‚Üí      RateLimiter.instance = new RateLimiter();
 88‚Üí    }
 89‚Üí    return RateLimiter.instance;
 90‚Üí  }
 91‚Üí
 92‚Üí  /**
 93‚Üí   * Check if request should be rate limited
 94‚Üí   */
 95‚Üí  async checkRateLimit(
 96‚Üí    request: NextRequest,
 97‚Üí    identifier?: string,
 98‚Üí    customConfig?: RateLimitConfig
 99‚Üí  ): Promise<RateLimitResult> {
100‚Üí    const route = this.normalizeRoute(request.nextUrl.pathname);
101‚Üí    const config = customConfig || this.getRouteConfig(route);
102‚Üí    
103‚Üí    // Skip rate limiting if specified
104‚Üí    if (config.skipIf && config.skipIf(request)) {
105‚Üí      return {
106‚Üí        success: true,
107‚Üí        limit: config.requests,
108‚Üí        remaining: config.requests,
109‚Üí        resetTime: Date.now() + config.window
110‚Üí      };
111‚Üí    }
112‚Üí
113‚Üí    const key = this.generateKey(request, route, identifier);
114‚Üí    const now = Date.now();
115‚Üí    
116‚Üí    let entry = this.requestCounts.get(key);
117‚Üí    
118‚Üí    if (!entry || now >= entry.resetTime) {
119‚Üí      // Create new or reset expired entry
120‚Üí      entry = {
121‚Üí        count: 1,
122‚Üí        resetTime: now + config.window
123‚Üí      };
124‚Üí      this.requestCounts.set(key, entry);
125‚Üí      
126‚Üí      return {
127‚Üí        success: true,
128‚Üí        limit: config.requests,
129‚Üí        remaining: config.requests - 1,
130‚Üí        resetTime: entry.resetTime
131‚Üí      };
132‚Üí    }
133‚Üí
134‚Üí    // Increment existing entry
135‚Üí    entry.count++;
136‚Üí    
137‚Üí    if (entry.count > config.requests) {
138‚Üí      // Rate limit exceeded
139‚Üí      await this.handleRateLimitExceeded(request, route, config, identifier);
140‚Üí      
141‚Üí      return {
142‚Üí        success: false,
143‚Üí        limit: config.requests,
144‚Üí        remaining: 0,
145‚Üí        resetTime: entry.resetTime,
146‚Üí        retryAfter: Math.ceil((entry.resetTime - now) / 1000)
147‚Üí      };
148‚Üí    }
149‚Üí
150‚Üí    return {
151‚Üí      success: true,
152‚Üí      limit: config.requests,
153‚Üí      remaining: config.requests - entry.count,
154‚Üí      resetTime: entry.resetTime
155‚Üí    };
156‚Üí  }
157‚Üí
158‚Üí  /**
159‚Üí   * Apply rate limiting to API route
160‚Üí   */
161‚Üí  async applyRateLimit(
162‚Üí    request: NextRequest,
163‚Üí    identifier?: string,
164‚Üí    customConfig?: RateLimitConfig
165‚Üí  ): Promise<NextResponse | null> {
166‚Üí    const result = await this.checkRateLimit(request, identifier, customConfig);
167‚Üí    
168‚Üí    if (!result.success) {
169‚Üí      const config = customConfig || this.getRouteConfig(this.normalizeRoute(request.nextUrl.pathname));
170‚Üí      
171‚Üí      return NextResponse.json(
172‚Üí        {
173‚Üí          error: 'Rate limit exceeded',
174‚Üí          message: config.message || 'Too many requests. Please try again later.',
175‚Üí          retryAfter: result.retryAfter
176‚Üí        },
177‚Üí        {
178‚Üí          status: 429,
179‚Üí          headers: {
180‚Üí            'X-RateLimit-Limit': result.limit.toString(),
181‚Üí            'X-RateLimit-Remaining': result.remaining.toString(),
182‚Üí            'X-RateLimit-Reset': result.resetTime.toString(),
183‚Üí            'Retry-After': result.retryAfter?.toString() || '60'
184‚Üí          }
185‚Üí        }
186‚Üí      );
187‚Üí    }
188‚Üí
189‚Üí    return null; // No rate limit response needed
190‚Üí  }
191‚Üí
192‚Üí  /**
193‚Üí   * Add rate limit headers to response
194‚Üí   */
195‚Üí  addRateLimitHeaders(response: NextResponse, result: RateLimitResult): NextResponse {
196‚Üí    response.headers.set('X-RateLimit-Limit', result.limit.toString());
197‚Üí    response.headers.set('X-RateLimit-Remaining', result.remaining.toString());
198‚Üí    response.headers.set('X-RateLimit-Reset', result.resetTime.toString());
199‚Üí    
200‚Üí    if (!result.success && result.retryAfter) {
201‚Üí      response.headers.set('Retry-After', result.retryAfter.toString());
202‚Üí    }
203‚Üí    
204‚Üí    return response;
205‚Üí  }
206‚Üí
207‚Üí  private normalizeRoute(pathname: string): string {
208‚Üí    return pathname
209‚Üí      .replace(/\/api\/users\/[^\/]+$/, '/api/users/[id]')
210‚Üí      .replace(/\/api\/payrolls\/[^\/]+$/, '/api/payrolls/[id]')
211‚Üí      .replace(/\/api\/payroll-dates\/[^\/]+$/, '/api/payroll-dates/[payrollId]')
212‚Üí      .replace(/\/api\/staff\/[^\/]+$/, '/api/staff/[id]')
213‚Üí      .replace(/\/api\/developer\/[^\/]+/, '/api/developer/[action]');
214‚Üí  }
215‚Üí
216‚Üí  private getRouteConfig(route: string): RateLimitConfig {
217‚Üí    return this.ROUTE_LIMITS[route] || this.ROUTE_LIMITS.default;
218‚Üí  }
219‚Üí
220‚Üí  private generateKey(request: NextRequest, route: string, identifier?: string): string {
221‚Üí    const ip = this.getClientIP(request);
222‚Üí    const userKey = identifier || ip;
223‚Üí    return `${route}:${userKey}`;
224‚Üí  }
225‚Üí
226‚Üí  private getClientIP(request: NextRequest): string {
227‚Üí    return request.headers.get('x-forwarded-for')?.split(',')[0] ||
228‚Üí           request.headers.get('x-real-ip') ||
229‚Üí           request.headers.get('cf-connecting-ip') ||
230‚Üí           'unknown';
231‚Üí  }
232‚Üí
233‚Üí  private async handleRateLimitExceeded(
234‚Üí    request: NextRequest,
235‚Üí    route: string,
236‚Üí    config: RateLimitConfig,
237‚Üí    identifier?: string
238‚Üí  ): Promise<void> {
239‚Üí    const ip = this.getClientIP(request);
240‚Üí    
241‚Üí    // Log rate limit violation
242‚Üí    console.warn(`Rate limit exceeded: ${route} for ${identifier || ip}`);
243‚Üí    
244‚Üí    // Notify enhanced route monitor
245‚Üí    await this.monitor.monitorRequest(request, identifier, Date.now(), false, {
246‚Üí      rateLimitExceeded: true,
247‚Üí      limit: config.requests,
248‚Üí      window: config.window
249‚Üí    });
250‚Üí  }
251‚Üí
252‚Üí  private cleanup(): void {
253‚Üí    const now = Date.now();
254‚Üí    
255‚Üí    for (const [key, entry] of this.requestCounts.entries()) {
256‚Üí      if (now >= entry.resetTime) {
257‚Üí        this.requestCounts.delete(key);
258‚Üí      }
259‚Üí    }
260‚Üí  }
261‚Üí
262‚Üí  // Utility methods for testing and monitoring
263‚Üí  getCurrentCounts(): Map<string, { count: number; resetTime: number }> {
