// app/providers.tsx
"use client";

import React from "react";
import { ClerkProvider, useAuth } from "@clerk/nextjs";
import { ApolloProvider } from "@apollo/client";
import client from "@/lib/apollo/simple-client";
import { AuthProvider } from "@/lib/auth-context";
import { ErrorBoundary } from "@/components/error-boundary";
import { StrictDatabaseGuard } from "@/components/auth/StrictDatabaseGuard";
// Simple client-side audit logging (could be enhanced to send to API)
const clientAuditLog = (event: { userId: string; action: string; resource: string }) => {
  console.log('üîç CLIENT AUDIT:', {
    timestamp: new Date().toISOString(),
    ...event
  });
  // TODO: Send to audit API endpoint in production
};

// ================================
// TYPES
// ================================

interface ProvidersProps {
  children: React.ReactNode;
}

// ================================
// AUTHENTICATED APOLLO PROVIDER
// ================================

function AuthenticatedApolloProvider({
  children,
}: {
  children: React.ReactNode;
}) {
  const { isLoaded, isSignedIn, userId } = useAuth();

  // Wait for Clerk to finish loading before providing Apollo client
  if (!isLoaded) {
    return <div>Loading...</div>;
  }

  // Clear Apollo cache on sign out for SOC2 compliance
  React.useEffect(() => {
    if (!isSignedIn && userId) {
      clientAuditLog({
        userId,
        action: 'user_signed_out_global',
        resource: 'authentication',
      });
      client.clearStore();
    }
  }, [isSignedIn, userId]);

  return <ApolloProvider client={client}>{children}</ApolloProvider>;
}

// ================================
// PROVIDERS COMPONENT
// ================================

export function Providers({ children }: ProvidersProps) {
  return (
    <ErrorBoundary>
      <ClerkProvider
        signInUrl="/sign-in"
        signUpUrl="/sign-up"
        signInFallbackRedirectUrl="/dashboard"
        signUpFallbackRedirectUrl="/dashboard"
      >
        <AuthenticatedApolloProvider>
          <AuthProvider>
            <StrictDatabaseGuard>
              {children}
            </StrictDatabaseGuard>
          </AuthProvider>
        </AuthenticatedApolloProvider>
      </ClerkProvider>
    </ErrorBoundary>
  );
}

