104‚Üí  DEVELOPER_TOOLS: "developer_tools",
105‚Üí} as const;
106‚Üí
107‚Üí// Role-permission mapping
108‚Üíconst ROLE_PERMISSIONS: Record<UserRole, string[]> = {
109‚Üí  developer: [
110‚Üí    PERMISSIONS.VIEW_DASHBOARD,
111‚Üí    PERMISSIONS.MANAGE_STAFF,
112‚Üí    PERMISSIONS.VIEW_STAFF,
113‚Üí    PERMISSIONS.MANAGE_CLIENTS,
114‚Üí    PERMISSIONS.VIEW_CLIENTS,
115‚Üí    PERMISSIONS.PROCESS_PAYROLLS,
116‚Üí    PERMISSIONS.APPROVE_PAYROLLS,
117‚Üí    PERMISSIONS.VIEW_PAYROLLS,
118‚Üí    PERMISSIONS.VIEW_FINANCIALS,
119‚Üí    PERMISSIONS.MANAGE_BILLING,
120‚Üí    PERMISSIONS.VIEW_REPORTS,
121‚Üí    PERMISSIONS.GENERATE_REPORTS,
122‚Üí    PERMISSIONS.SYSTEM_ADMIN,
123‚Üí    PERMISSIONS.MANAGE_SETTINGS,
124‚Üí    PERMISSIONS.MANAGE_ROLES,
125‚Üí    PERMISSIONS.MANAGE_USERS,
126‚Üí    PERMISSIONS.INVITE_USERS,
127‚Üí    PERMISSIONS.DEVELOPER_TOOLS,
128‚Üí  ],
129‚Üí  org_admin: [
130‚Üí    PERMISSIONS.VIEW_DASHBOARD,
131‚Üí    PERMISSIONS.MANAGE_STAFF,
132‚Üí    PERMISSIONS.VIEW_STAFF,
133‚Üí    PERMISSIONS.MANAGE_CLIENTS,
134‚Üí    PERMISSIONS.VIEW_CLIENTS,
135‚Üí    PERMISSIONS.PROCESS_PAYROLLS,
136‚Üí    PERMISSIONS.APPROVE_PAYROLLS,
137‚Üí    PERMISSIONS.VIEW_PAYROLLS,
138‚Üí    PERMISSIONS.VIEW_FINANCIALS,
139‚Üí    PERMISSIONS.MANAGE_BILLING,
140‚Üí    PERMISSIONS.VIEW_REPORTS,
141‚Üí    PERMISSIONS.GENERATE_REPORTS,
142‚Üí    PERMISSIONS.MANAGE_SETTINGS,
143‚Üí    PERMISSIONS.MANAGE_ROLES,
144‚Üí    PERMISSIONS.MANAGE_USERS,
145‚Üí    PERMISSIONS.INVITE_USERS,
146‚Üí  ],
147‚Üí  manager: [
148‚Üí    PERMISSIONS.VIEW_DASHBOARD,
149‚Üí    PERMISSIONS.MANAGE_STAFF,
150‚Üí    PERMISSIONS.VIEW_STAFF,
151‚Üí    PERMISSIONS.MANAGE_CLIENTS,
152‚Üí    PERMISSIONS.VIEW_CLIENTS,
153‚Üí    PERMISSIONS.PROCESS_PAYROLLS,
154‚Üí    PERMISSIONS.APPROVE_PAYROLLS,
155‚Üí    PERMISSIONS.VIEW_PAYROLLS,
156‚Üí    PERMISSIONS.VIEW_FINANCIALS,
157‚Üí    PERMISSIONS.VIEW_REPORTS,
158‚Üí    PERMISSIONS.GENERATE_REPORTS,
159‚Üí    PERMISSIONS.INVITE_USERS,
160‚Üí  ],
161‚Üí  consultant: [
162‚Üí    PERMISSIONS.VIEW_DASHBOARD,
163‚Üí    PERMISSIONS.VIEW_STAFF,
164‚Üí    PERMISSIONS.VIEW_CLIENTS,
165‚Üí    PERMISSIONS.PROCESS_PAYROLLS,
166‚Üí    PERMISSIONS.VIEW_PAYROLLS,
167‚Üí    PERMISSIONS.VIEW_REPORTS,
168‚Üí  ],
169‚Üí  viewer: [
170‚Üí    PERMISSIONS.VIEW_DASHBOARD,
171‚Üí    PERMISSIONS.VIEW_CLIENTS,
172‚Üí    PERMISSIONS.VIEW_PAYROLLS,
173‚Üí    PERMISSIONS.VIEW_REPORTS,
174‚Üí  ],
175‚Üí};
176‚Üí
177‚Üí// Route permissions mapping
178‚Üíexport const ROUTE_PERMISSIONS: Record<string, string[]> = {
179‚Üí  "/dashboard": [PERMISSIONS.VIEW_DASHBOARD],
180‚Üí  "/staff": [PERMISSIONS.VIEW_STAFF],
181‚Üí  "/staff/new": [PERMISSIONS.MANAGE_STAFF],
182‚Üí  "/clients": [PERMISSIONS.VIEW_CLIENTS],
183‚Üí  "/clients/new": [PERMISSIONS.MANAGE_CLIENTS],
184‚Üí  "/payrolls": [PERMISSIONS.VIEW_PAYROLLS],
185‚Üí  "/payroll-schedule": [PERMISSIONS.PROCESS_PAYROLLS],
186‚Üí  "/settings": [PERMISSIONS.MANAGE_SETTINGS],
187‚Üí  "/developer": [PERMISSIONS.DEVELOPER_TOOLS],
188‚Üí  "/ai-assistant": [PERMISSIONS.VIEW_DASHBOARD],
189‚Üí  "/calendar": [PERMISSIONS.VIEW_DASHBOARD],
190‚Üí  "/tax-calculator": [PERMISSIONS.VIEW_DASHBOARD],
191‚Üí};
192‚Üí
193‚Üíconst AuthContext = createContext<AuthContextType | undefined>(undefined);
194‚Üí
195‚Üíexport function AuthProvider({ children }: { children: React.ReactNode }) {
196‚Üí  const {
197‚Üí    isLoaded: isClerkLoaded,
198‚Üí    userId,
199‚Üí    isSignedIn,
200‚Üí    signOut: clerkSignOut,
201‚Üí    getToken,
202‚Üí  } = useAuth();
203‚Üí  const { user } = useUser();
204‚Üí  const {
205‚Üí    currentUser: databaseUser,
206‚Üí    loading: dbUserLoading,
207‚Üí    extractionAttempts,
208‚Üí  } = useCurrentUser();
209‚Üí  const [userRole, setUserRole] = useState<UserRole>("viewer");
210‚Üí  const [isRoleLoading, setIsRoleLoading] = useState(true);
211‚Üí  const [permissions, setPermissions] = useState<string[]>([]);
212‚Üí  const [hasValidDatabaseUser, setHasValidDatabaseUser] = useState(false);
213‚Üí  const [validationState, setValidationState] = useState({
214‚Üí    lastValidationTime: 0,
215‚Üí    validationInProgress: false,
216‚Üí    consecutiveFailures: 0,
217‚Üí  });
218‚Üí
219‚Üí  // Database user validation with mutex protection
220‚Üí  const validateDatabaseUser = useCallback(async () => {
221‚Üí    if (!isSignedIn || !isClerkLoaded || dbUserLoading) {
222‚Üí      return;
223‚Üí    }
224‚Üí
225‚Üí    const now = Date.now();
226‚Üí
227‚Üí    // Skip if validation is in progress or too recent (increased to 30 seconds)
228‚Üí    if (
229‚Üí      validationState.validationInProgress ||
230‚Üí      now - validationState.lastValidationTime < 30000
231‚Üí    ) {
232‚Üí      return;
233‚Üí    }
234‚Üí
235‚Üí    // Skip if too many consecutive failures (increased threshold)
236‚Üí    if (validationState.consecutiveFailures >= 5) {
237‚Üí      console.warn("üö´ Skipping validation due to consecutive failures");
238‚Üí      return;
239‚Üí    }
240‚Üí
241‚Üí    try {
242‚Üí      await authMutex.acquire(
243‚Üí        `user-validation-${userId}-${now}`,
244‚Üí        "session_validation",
245‚Üí        async () => {
246‚Üí          setValidationState((prev) => ({
247‚Üí            ...prev,
248‚Üí            validationInProgress: true,
249‚Üí            lastValidationTime: now,
250‚Üí          }));
251‚Üí
252‚Üí          const newValidStatus = !!databaseUser;
253‚Üí
254‚Üí          // Only update state if it actually changed
255‚Üí          if (hasValidDatabaseUser !== newValidStatus) {
256‚Üí            setHasValidDatabaseUser(newValidStatus);
257‚Üí          }
258‚Üí
259‚Üí          if (newValidStatus && databaseUser) {
260‚Üí            // If we have a database user, use their role directly
261‚Üí            const newRole = databaseUser.role as UserRole;
262‚Üí            if (userRole !== newRole) {
263‚Üí              setUserRole(newRole);
264‚Üí            }
265‚Üí            setIsRoleLoading(false);
266‚Üí
267‚Üí            // Reset failure count on success
268‚Üí            setValidationState((prev) => ({
269‚Üí              ...prev,
270‚Üí              consecutiveFailures: 0,
271‚Üí            }));
272‚Üí          } else {
273‚Üí            // Only log warning if we've tried multiple times and still no user
274‚Üí            if (extractionAttempts >= 2) {
275‚Üí              console.warn(
276‚Üí                "üö® AuthContext - SECURITY: Authenticated user not found in database after multiple attempts",
277‚Üí                {
278‚Üí                  clerkUserId: userId,
279‚Üí                  userEmail: user?.emailAddresses?.[0]?.emailAddress,
280‚Üí                  extractionAttempts,
281‚Üí                }
282‚Üí              );
283‚Üí            }
284‚Üí
285‚Üí            // SECURITY: Do not fall back to JWT role without database user
286‚Üí            // This prevents permission bypass attacks
287‚Üí            console.warn(
288‚Üí              "üö® SECURITY: User authenticated but not found in database - access will be restricted"
289‚Üí            );
290‚Üí            // Force role to viewer only for security
291‚Üí            setUserRole("viewer");
292‚Üí            setIsRoleLoading(false);
293‚Üí
294‚Üí            // Increment failure count
295‚Üí            setValidationState((prev) => ({
296‚Üí              ...prev,
297‚Üí              consecutiveFailures: prev.consecutiveFailures + 1,
298‚Üí            }));
299‚Üí          }
300‚Üí
301‚Üí          return true;
302‚Üí        }
303‚Üí      );
304‚Üí    } catch (error) {
305‚Üí      console.error("‚ùå User validation failed:", error);
306‚Üí      setValidationState((prev) => ({
307‚Üí        ...prev,
308‚Üí        consecutiveFailures: prev.consecutiveFailures + 1,
309‚Üí      }));
310‚Üí    } finally {
311‚Üí      setValidationState((prev) => ({
312‚Üí        ...prev,
313‚Üí        validationInProgress: false,
314‚Üí      }));
315‚Üí    }
316‚Üí  }, [
317‚Üí    isSignedIn,
318‚Üí    isClerkLoaded,
319‚Üí    dbUserLoading,
320‚Üí    databaseUser,
321‚Üí    userId,
322‚Üí    user,
323‚Üí    extractionAttempts,
324‚Üí    hasValidDatabaseUser,
325‚Üí    userRole,
326‚Üí    isRoleLoading,
327‚Üí    validationState.validationInProgress,
328‚Üí    validationState.lastValidationTime,
329‚Üí    validationState.consecutiveFailures,
330‚Üí  ]);
331‚Üí
332‚Üí  // Handle user sign out state
333‚Üí  const handleSignOutState = useCallback(() => {
334‚Üí    if (!isSignedIn && isClerkLoaded) {
335‚Üí      // User signed out - reset state
336‚Üí      if (hasValidDatabaseUser || userRole !== "viewer") {
337‚Üí        setHasValidDatabaseUser(false);
338‚Üí        setUserRole("viewer");
339‚Üí        setIsRoleLoading(false);
340‚Üí        setValidationState({
341‚Üí          lastValidationTime: 0,
342‚Üí          validationInProgress: false,
343‚Üí          consecutiveFailures: 0,
344‚Üí        });
345‚Üí      }
346‚Üí    }
347‚Üí  }, [isSignedIn, isClerkLoaded, hasValidDatabaseUser, userRole]);
348‚Üí
349‚Üí  // Monitor database user status
350‚Üí  useEffect(() => {
351‚Üí    if (isSignedIn) {
352‚Üí      validateDatabaseUser();
353‚Üí    } else {
354‚Üí      handleSignOutState();
355‚Üí    }
356‚Üí  }, [isSignedIn, databaseUser, validateDatabaseUser, handleSignOutState]);
357‚Üí
358‚Üí  // Fetch user role from JWT token (only used as fallback) with mutex protection
359‚Üí  const fetchUserRole = useCallback(async () => {
360‚Üí    if (!isClerkLoaded || !userId) {
361‚Üí      setIsRoleLoading(false);
362‚Üí      return;
363‚Üí    }
364‚Üí
365‚Üí    try {
366‚Üí      await authMutex.acquire(
367‚Üí        `role-fetch-${userId}-${Date.now()}`,
368‚Üí        "token_refresh",
369‚Üí        async () => {
370‚Üí          setIsRoleLoading(true);
371‚Üí          const token = await getToken({ template: "hasura" });
372‚Üí          if (token) {
373‚Üí            const payload = JSON.parse(atob(token.split(".")[1]));
374‚Üí            const claims = payload["https://hasura.io/jwt/claims"];
375‚Üí            const role = claims?.["x-hasura-default-role"] as UserRole;
376‚Üí
377‚Üí            if (role && ROLE_PERMISSIONS[role]) {
378‚Üí              setUserRole(role);
379‚Üí            } else {
380‚Üí              setUserRole("viewer");
381‚Üí            }
382‚Üí          } else {
383‚Üí            setUserRole("viewer");
384‚Üí          }
385‚Üí          return true;
386‚Üí        }
387‚Üí      );
388‚Üí    } catch (error) {
389‚Üí      console.error("Error fetching user role:", error);
390‚Üí      setUserRole("viewer");
391‚Üí    } finally {
392‚Üí      setIsRoleLoading(false);
393‚Üí    }
394‚Üí  }, [isClerkLoaded, userId, getToken]);
395‚Üí
396‚Üí  // Memoize permissions to prevent unnecessary recalculations
397‚Üí  const memoizedPermissions = useMemo(() => {
398‚Üí    return ROLE_PERMISSIONS[userRole] || [];
399‚Üí  }, [userRole]);
400‚Üí
401‚Üí  // Update permissions when memoized permissions change
402‚Üí  useEffect(() => {
403‚Üí    if (JSON.stringify(permissions) !== JSON.stringify(memoizedPermissions)) {
404‚Üí      setPermissions(memoizedPermissions);
405‚Üí    }
406‚Üí  }, [memoizedPermissions, permissions]);
407‚Üí
408‚Üí  // Permission check functions - STRICT security-aware
409‚Üí  const hasPermission = (permission: string): boolean => {
410‚Üí    // SECURITY: ALWAYS deny if not authenticated
411‚Üí    if (!isSignedIn || !isClerkLoaded) {
412‚Üí      return false;
413‚Üí    }
414‚Üí
415‚Üí    // SECURITY: ALWAYS deny if user doesn't exist in database
416‚Üí    if (!hasValidDatabaseUser) {
417‚Üí      // Log warning for debugging but don't spam
418‚Üí      if (Math.random() < 0.1) {
419‚Üí        console.warn(
420‚Üí          "üö® SECURITY: Permission denied - User not found in database",
421‚Üí          {
422‚Üí            isSignedIn,
423‚Üí            hasValidDatabaseUser,
424‚Üí            userId: userId?.substring(0, 8) + "..." || "none",
425‚Üí          }
426‚Üí        );
427‚Üí      }
428‚Üí      return false;
429‚Üí    }
430‚Üí
431‚Üí    // SECURITY: Only grant permissions if we have a valid role and permissions
432‚Üí    if (!userRole || userRole === "viewer") {
433‚Üí      return permission === "view_dashboard"; // Only allow basic dashboard view
434‚Üí    }
435‚Üí
436‚Üí    return permissions.includes(permission);
437‚Üí  };
438‚Üí
439‚Üí  const hasAnyPermission = (requiredPermissions: string[]): boolean => {
440‚Üí    // SECURITY: Use the strict hasPermission check for each permission
441‚Üí    return requiredPermissions.some((permission) => hasPermission(permission));
442‚Üí  };
443‚Üí
444‚Üí  const hasRole = (roles: UserRole[]): boolean => {
445‚Üí    // SECURITY: ALWAYS deny if not authenticated
446‚Üí    if (!isSignedIn || !isClerkLoaded) {
447‚Üí      return false;
448‚Üí    }
449‚Üí
450‚Üí    // SECURITY: ALWAYS deny if user doesn't exist in database
451‚Üí    if (!hasValidDatabaseUser) {
452‚Üí      if (Math.random() < 0.1) {
453‚Üí        console.warn(
454‚Üí          "üö® SECURITY: Role check denied - User not found in database",
455‚Üí          {
456‚Üí            requestedRoles: roles,
457‚Üí            currentRole: userRole,
458‚Üí            hasValidDatabaseUser,
459‚Üí          }
460‚Üí        );
461‚Üí      }
462‚Üí      return false;
463‚Üí    }
464‚Üí
465‚Üí    // SECURITY: Only grant roles if we have a valid user role
466‚Üí    if (!userRole) {
467‚Üí      return false;
468‚Üí    }
469‚Üí
470‚Üí    return roles.includes(userRole);
471‚Üí  };
472‚Üí
473‚Üí  // Computed properties for common checks - STRICT security
474‚Üí  const hasAdminAccess: boolean = !!(
475‚Üí    isSignedIn &&
476‚Üí    isClerkLoaded &&
477‚Üí    hasValidDatabaseUser &&
478‚Üí    userRole &&
479‚Üí    (userRole === "developer" || userRole === "org_admin")
480‚Üí  );
481‚Üí
482‚Üí  const canManageUsers = hasPermission(PERMISSIONS.MANAGE_USERS);
483‚Üí  const canManageClients = hasPermission(PERMISSIONS.MANAGE_CLIENTS);
484‚Üí  const canProcessPayrolls = hasPermission(PERMISSIONS.PROCESS_PAYROLLS);
485‚Üí  const canViewFinancials = hasPermission(PERMISSIONS.VIEW_FINANCIALS);
486‚Üí
487‚Üí  // Enhanced sign out
488‚Üí  const signOut = async () => {
489‚Üí    try {
490‚Üí      await clerkSignOut();
491‚Üí    } catch (error) {
492‚Üí      console.error("Error signing out:", error);
493‚Üí    }
494‚Üí  };
495‚Üí
496‚Üí  // Refresh user data with mutex protection
497‚Üí  const refreshUserData = useCallback(async () => {
498‚Üí    if (!userId) return;
499‚Üí
500‚Üí    try {
501‚Üí      await authMutex.acquire(
502‚Üí        `user-refresh-${userId}-${Date.now()}`,
503‚Üí        "session_validation",
504‚Üí        async () => {
505‚Üí          setIsRoleLoading(true);
506‚Üí
507‚Üí          // Force token refresh using the centralized token manager
508‚Üí          const token = await centralizedTokenManager.forceRefresh(
509‚Üí            () => getToken({ template: "hasura" }),
510‚Üí            userId
