 34‚Üí      return () => clearTimeout(timer);
 35‚Üí    }
 36‚Üí  }, [clerkLoaded, clerkUser, gracePeriodEnded]);
 37‚Üí
 38‚Üí  // Monitor access status
 39‚Üí  useEffect(() => {
 40‚Üí    // Wait for Clerk to load
