 48‚Üí      setBlockReason("");
 49‚Üí      return;
 50‚Üí    }
 51‚Üí
 52‚Üí    // Still loading database check OR initial user ID extraction, or within grace period
 53‚Üí    if (dbUserLoading || !isReady || !gracePeriodEnded) {
 54‚Üí      return;
 55‚Üí    }
 56‚Üí
 57‚Üí    // Database error - block as precaution
 58‚Üí    if (dbUserError) {
