import React, { useEffect } from 'react';

// Test the patterns we fixed
const TestComponent = ({ isSignedIn, userId, databaseUser }) => {
  // This is the pattern we FIXED in AuthenticatedApolloProvider
  useEffect(() => {
    if (!isSignedIn) {
      const hadUser = localStorage.getItem('clerk_had_user');
      if (hadUser) {
        console.log('Clean logout detected');
        localStorage.removeItem('clerk_had_user');
      }
    } else if (userId) {
      localStorage.setItem('clerk_had_user', userId);
    }
  }, [isSignedIn, userId]); // Clean dependencies

  // This is the pattern we FIXED in AuthProvider
  useEffect(() => {
    if (isSignedIn) {
      console.log('User validation triggered');
    }
  }, [isSignedIn, databaseUser]); // Simplified dependencies

  return null;
};

console.log('‚úÖ useEffect patterns validated');

