  2‚Üí"use client";
  3‚Üí
  4‚Üíimport { useUser, useClerk } from "@clerk/nextjs";
  5‚Üíimport { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
  6‚Üíimport { Button } from "@/components/ui/button";
  7‚Üíimport {
  8‚Üí  DropdownMenu,
  9‚Üí  DropdownMenuContent,
 10‚Üí  DropdownMenuGroup,
 11‚Üí  DropdownMenuItem,
 12‚Üí  DropdownMenuLabel,
 13‚Üí  DropdownMenuSeparator,
 14‚Üí  DropdownMenuTrigger,
 15‚Üí} from "@/components/ui/dropdown-menu";
 16‚Üíimport { useRouter } from "next/navigation";
 17‚Üí
 18‚Üíexport function UserNav() {
 19‚Üí  const router = useRouter();
 20‚Üí  const { user, isLoaded } = useUser();
 21‚Üí  const { signOut } = useClerk();
 22‚Üí
 23‚Üí  // Helper function to get the correct avatar image (same logic as settings page)
 24‚Üí  const getAvatarImage = () => {
 25‚Üí    if (!user) return "";
 26‚Üí
 27‚Üí    // If user has uploaded a custom image, use that
 28‚Üí    if (user.hasImage && user.imageUrl) {
 29‚Üí      return user.imageUrl;
 30‚Üí    }
 31‚Üí
 32‚Üí    // If user has external accounts (like Google) with avatar, use that
 33‚Üí    if (user.externalAccounts && user.externalAccounts.length > 0) {
 34‚Üí      const externalAccount = user.externalAccounts[0];
 35‚Üí      if (externalAccount.imageUrl) {
 36‚Üí        return externalAccount.imageUrl;
 37‚Üí      }
 38‚Üí    }
 39‚Üí
 40‚Üí    // Fallback to empty string for default avatar
 41‚Üí    return "";
 42‚Üí  };
 43‚Üí
 44‚Üí  // Get user initials for avatar fallback
 45‚Üí  const getUserInitials = () => {
 46‚Üí    if (!isLoaded || !user?.fullName) return "U";
 47‚Üí
 48‚Üí    // Split the name and get initials (up to 2 characters)
 49‚Üí    const nameParts = user.fullName.split(" ");
 50‚Üí    if (nameParts.length === 1) return nameParts[0].charAt(0).toUpperCase();
 51‚Üí    return (nameParts[0].charAt(0) + nameParts[1].charAt(0)).toUpperCase();
 52‚Üí  };
 53‚Üí
 54‚Üí  const handleSignOut = async () => {
 55‚Üí    await signOut();
 56‚Üí    router.push("/");
 57‚Üí  };
 58‚Üí
 59‚Üí  return (
 60‚Üí    <DropdownMenu>
 61‚Üí      <DropdownMenuTrigger asChild>
 62‚Üí        <Button
 63‚Üí          variant="ghost"
 64‚Üí          className="relative h-8 flex items-center gap-2 rounded-full"
 65‚Üí        >
 66‚Üí          <span className="hidden md:inline-block text-sm font-medium">
 67‚Üí            {isLoaded && user?.fullName ? user.fullName : ""}
 68‚Üí          </span>
 69‚Üí          <Avatar className="h-8 w-8">
 70‚Üí            <AvatarImage
 71‚Üí              src={getAvatarImage()}
 72‚Üí              alt={user?.fullName || "User avatar"}
 73‚Üí            />
 74‚Üí            <AvatarFallback>{getUserInitials()}</AvatarFallback>
 75‚Üí          </Avatar>
 76‚Üí        </Button>
 77‚Üí      </DropdownMenuTrigger>
 78‚Üí      <DropdownMenuContent className="w-56" align="end" forceMount>
 79‚Üí        <DropdownMenuLabel className="font-normal">
