198‚Üí    userId,
199‚Üí    isSignedIn,
200‚Üí    signOut: clerkSignOut,
201‚Üí    getToken,
202‚Üí  } = useAuth();
203‚Üí  const { user } = useUser();
204‚Üí  const {
205‚Üí    currentUser: databaseUser,
206‚Üí    loading: dbUserLoading,
207‚Üí    extractionAttempts,
208‚Üí  } = useCurrentUser();
209‚Üí  const [userRole, setUserRole] = useState<UserRole>("viewer");
210‚Üí  const [isRoleLoading, setIsRoleLoading] = useState(true);
211‚Üí  const [permissions, setPermissions] = useState<string[]>([]);
212‚Üí  const [hasValidDatabaseUser, setHasValidDatabaseUser] = useState(false);
213‚Üí  const [validationState, setValidationState] = useState({
214‚Üí    lastValidationTime: 0,
215‚Üí    validationInProgress: false,
216‚Üí    consecutiveFailures: 0,
217‚Üí  });
218‚Üí
219‚Üí  // Database user validation with mutex protection
220‚Üí  const validateDatabaseUser = useCallback(async () => {
221‚Üí    if (!isSignedIn || !isClerkLoaded || dbUserLoading) {
222‚Üí      return;
223‚Üí    }
224‚Üí
225‚Üí    const now = Date.now();
226‚Üí
227‚Üí    // Skip if validation is in progress or too recent (increased to 30 seconds)
228‚Üí    if (
229‚Üí      validationState.validationInProgress ||
230‚Üí      now - validationState.lastValidationTime < 30000
231‚Üí    ) {
232‚Üí      return;
233‚Üí    }
234‚Üí
235‚Üí    // Skip if too many consecutive failures (increased threshold)
236‚Üí    if (validationState.consecutiveFailures >= 5) {
237‚Üí      console.warn("üö´ Skipping validation due to consecutive failures");
238‚Üí      return;
239‚Üí    }
240‚Üí
241‚Üí    try {
242‚Üí      await authMutex.acquire(
243‚Üí        `user-validation-${userId}-${now}`,
244‚Üí        "session_validation",
245‚Üí        async () => {
246‚Üí          setValidationState((prev) => ({
247‚Üí            ...prev,
248‚Üí            validationInProgress: true,
249‚Üí            lastValidationTime: now,
250‚Üí          }));
251‚Üí
252‚Üí          const newValidStatus = !!databaseUser;
253‚Üí
254‚Üí          // Only update state if it actually changed
255‚Üí          if (hasValidDatabaseUser !== newValidStatus) {
256‚Üí            setHasValidDatabaseUser(newValidStatus);
257‚Üí          }
258‚Üí
259‚Üí          if (newValidStatus && databaseUser) {
260‚Üí            // If we have a database user, use their role directly
261‚Üí            const newRole = databaseUser.role as UserRole;
262‚Üí            if (userRole !== newRole) {
263‚Üí              setUserRole(newRole);
264‚Üí            }
265‚Üí            setIsRoleLoading(false);
266‚Üí
267‚Üí            // Reset failure count on success
268‚Üí            setValidationState((prev) => ({
269‚Üí              ...prev,
270‚Üí              consecutiveFailures: 0,
271‚Üí            }));
272‚Üí          } else {
273‚Üí            // Only log warning if we've tried multiple times and still no user
274‚Üí            if (extractionAttempts >= 2) {
275‚Üí              console.warn(
276‚Üí                "üö® AuthContext - SECURITY: Authenticated user not found in database after multiple attempts",
277‚Üí                {
278‚Üí                  clerkUserId: userId,
279‚Üí                  userEmail: user?.emailAddresses?.[0]?.emailAddress,
280‚Üí                  extractionAttempts,
281‚Üí                }
282‚Üí              );
283‚Üí            }
284‚Üí
285‚Üí            // SECURITY: Do not fall back to JWT role without database user
286‚Üí            // This prevents permission bypass attacks
287‚Üí            console.warn(
288‚Üí              "üö® SECURITY: User authenticated but not found in database - access will be restricted"
289‚Üí            );
290‚Üí            // Force role to viewer only for security
291‚Üí            setUserRole("viewer");
292‚Üí            setIsRoleLoading(false);
293‚Üí
294‚Üí            // Increment failure count
295‚Üí            setValidationState((prev) => ({
296‚Üí              ...prev,
297‚Üí              consecutiveFailures: prev.consecutiveFailures + 1,
298‚Üí            }));
299‚Üí          }
300‚Üí
301‚Üí          return true;
302‚Üí        }
303‚Üí      );
304‚Üí    } catch (error) {
305‚Üí      console.error("‚ùå User validation failed:", error);
306‚Üí      setValidationState((prev) => ({
307‚Üí        ...prev,
308‚Üí        consecutiveFailures: prev.consecutiveFailures + 1,
309‚Üí      }));
310‚Üí    } finally {
311‚Üí      setValidationState((prev) => ({
312‚Üí        ...prev,
313‚Üí        validationInProgress: false,
314‚Üí      }));
315‚Üí    }
316‚Üí  }, [isSignedIn, isClerkLoaded, dbUserLoading, databaseUser, userId, extractionAttempts]);
317‚Üí
318‚Üí  // Handle user sign out state
319‚Üí  const handleSignOutState = useCallback(() => {
320‚Üí    if (!isSignedIn && isClerkLoaded) {
321‚Üí      // User signed out - reset state
322‚Üí      if (hasValidDatabaseUser || userRole !== "viewer") {
323‚Üí        setHasValidDatabaseUser(false);
324‚Üí        setUserRole("viewer");
325‚Üí        setIsRoleLoading(false);
326‚Üí        setValidationState({
327‚Üí          lastValidationTime: 0,
328‚Üí          validationInProgress: false,
329‚Üí          consecutiveFailures: 0,
330‚Üí        });
331‚Üí      }
332‚Üí    }
333‚Üí  }, [isSignedIn, isClerkLoaded, hasValidDatabaseUser, userRole]);
334‚Üí
335‚Üí  // Monitor database user status
336‚Üí  useEffect(() => {
337‚Üí    if (isSignedIn) {
338‚Üí      validateDatabaseUser();
339‚Üí    } else {
340‚Üí      handleSignOutState();
341‚Üí    }
342‚Üí  }, [isSignedIn, databaseUser]);
343‚Üí
344‚Üí  // Fetch user role from JWT token (only used as fallback) with mutex protection
345‚Üí  const fetchUserRole = useCallback(async () => {
346‚Üí    if (!isClerkLoaded || !userId) {
347‚Üí      setIsRoleLoading(false);
348‚Üí      return;
349‚Üí    }
350‚Üí
351‚Üí    try {
352‚Üí      await authMutex.acquire(
353‚Üí        `role-fetch-${userId}-${Date.now()}`,
354‚Üí        "token_refresh",
355‚Üí        async () => {
356‚Üí          setIsRoleLoading(true);
357‚Üí          const token = await getToken({ template: "hasura" });
358‚Üí          if (token) {
359‚Üí            const payload = JSON.parse(atob(token.split(".")[1]));
360‚Üí            const claims = payload["https://hasura.io/jwt/claims"];
361‚Üí            const role = claims?.["x-hasura-default-role"] as UserRole;
362‚Üí
363‚Üí            if (role && ROLE_PERMISSIONS[role]) {
364‚Üí              setUserRole(role);
365‚Üí            } else {
366‚Üí              setUserRole("viewer");
367‚Üí            }
368‚Üí          } else {
369‚Üí            setUserRole("viewer");
370‚Üí          }
371‚Üí          return true;
372‚Üí        }
373‚Üí      );
374‚Üí    } catch (error) {
375‚Üí      console.error("Error fetching user role:", error);
376‚Üí      setUserRole("viewer");
377‚Üí    } finally {
378‚Üí      setIsRoleLoading(false);
379‚Üí    }
380‚Üí  }, [isClerkLoaded, userId, getToken]);
381‚Üí
382‚Üí  // Memoize permissions to prevent unnecessary recalculations
383‚Üí  const memoizedPermissions = useMemo(() => {
384‚Üí    return ROLE_PERMISSIONS[userRole] || [];
385‚Üí  }, [userRole]);
386‚Üí
387‚Üí  // Update permissions when memoized permissions change
388‚Üí  useEffect(() => {
389‚Üí    setPermissions(memoizedPermissions);
390‚Üí  }, [memoizedPermissions]);
391‚Üí
392‚Üí  // Permission check functions - STRICT security-aware
393‚Üí  const hasPermission = (permission: string): boolean => {
394‚Üí    // SECURITY: ALWAYS deny if not authenticated
395‚Üí    if (!isSignedIn || !isClerkLoaded) {
396‚Üí      return false;
397‚Üí    }
398‚Üí
399‚Üí    // SECURITY: ALWAYS deny if user doesn't exist in database
400‚Üí    if (!hasValidDatabaseUser) {
401‚Üí      // Log warning for debugging but don't spam
402‚Üí      if (Math.random() < 0.1) {
403‚Üí        console.warn(
404‚Üí          "üö® SECURITY: Permission denied - User not found in database",
405‚Üí          {
406‚Üí            isSignedIn,
407‚Üí            hasValidDatabaseUser,
408‚Üí            userId: userId?.substring(0, 8) + "..." || "none",
409‚Üí          }
410‚Üí        );
411‚Üí      }
412‚Üí      return false;
413‚Üí    }
414‚Üí
415‚Üí    // SECURITY: Only grant permissions if we have a valid role and permissions
416‚Üí    if (!userRole || userRole === "viewer") {
417‚Üí      return permission === "view_dashboard"; // Only allow basic dashboard view
418‚Üí    }
419‚Üí
420‚Üí    return permissions.includes(permission);
421‚Üí  };
422‚Üí
423‚Üí  const hasAnyPermission = (requiredPermissions: string[]): boolean => {
424‚Üí    // SECURITY: Use the strict hasPermission check for each permission
425‚Üí    return requiredPermissions.some((permission) => hasPermission(permission));
426‚Üí  };
427‚Üí
428‚Üí  const hasRole = (roles: UserRole[]): boolean => {
429‚Üí    // SECURITY: ALWAYS deny if not authenticated
430‚Üí    if (!isSignedIn || !isClerkLoaded) {
431‚Üí      return false;
432‚Üí    }
433‚Üí
434‚Üí    // SECURITY: ALWAYS deny if user doesn't exist in database
435‚Üí    if (!hasValidDatabaseUser) {
436‚Üí      if (Math.random() < 0.1) {
437‚Üí        console.warn(
438‚Üí          "üö® SECURITY: Role check denied - User not found in database",
439‚Üí          {
440‚Üí            requestedRoles: roles,
441‚Üí            currentRole: userRole,
442‚Üí            hasValidDatabaseUser,
443‚Üí          }
444‚Üí        );
445‚Üí      }
446‚Üí      return false;
447‚Üí    }
448‚Üí
449‚Üí    // SECURITY: Only grant roles if we have a valid user role
450‚Üí    if (!userRole) {
451‚Üí      return false;
452‚Üí    }
453‚Üí
454‚Üí    return roles.includes(userRole);
455‚Üí  };
456‚Üí
457‚Üí  // Computed properties for common checks - STRICT security
458‚Üí  const hasAdminAccess: boolean = !!(
459‚Üí    isSignedIn &&
460‚Üí    isClerkLoaded &&
461‚Üí    hasValidDatabaseUser &&
462‚Üí    userRole &&
463‚Üí    (userRole === "developer" || userRole === "org_admin")
464‚Üí  );
465‚Üí
466‚Üí  const canManageUsers = hasPermission(PERMISSIONS.MANAGE_USERS);
467‚Üí  const canManageClients = hasPermission(PERMISSIONS.MANAGE_CLIENTS);
468‚Üí  const canProcessPayrolls = hasPermission(PERMISSIONS.PROCESS_PAYROLLS);
469‚Üí  const canViewFinancials = hasPermission(PERMISSIONS.VIEW_FINANCIALS);
470‚Üí
471‚Üí  // Enhanced sign out
472‚Üí  const signOut = async () => {
473‚Üí    try {
474‚Üí      await clerkSignOut();
475‚Üí    } catch (error) {
476‚Üí      console.error("Error signing out:", error);
477‚Üí    }
478‚Üí  };
479‚Üí
480‚Üí  // Refresh user data with mutex protection
481‚Üí  const refreshUserData = useCallback(async () => {
482‚Üí    if (!userId) return;
483‚Üí
484‚Üí    try {
485‚Üí      await authMutex.acquire(
486‚Üí        `user-refresh-${userId}-${Date.now()}`,
487‚Üí        "session_validation",
488‚Üí        async () => {
489‚Üí          setIsRoleLoading(true);
490‚Üí
491‚Üí          // Force token refresh using the centralized token manager
492‚Üí          const token = await centralizedTokenManager.forceRefresh(
493‚Üí            () => getToken({ template: "hasura" }),
494‚Üí            userId
495‚Üí          );
496‚Üí
497‚Üí          if (token) {
498‚Üí            const payload = JSON.parse(atob(token.split(".")[1]));
499‚Üí            const claims = payload["https://hasura.io/jwt/claims"];
500‚Üí            const role = claims?.["x-hasura-default-role"] as UserRole;
501‚Üí
502‚Üí            if (role && ROLE_PERMISSIONS[role]) {
503‚Üí              setUserRole(role);
504‚Üí            }
505‚Üí          }
506‚Üí
507‚Üí          // Reset validation state on successful refresh
508‚Üí          setValidationState((prev) => ({
509‚Üí            ...prev,
510‚Üí            consecutiveFailures: 0,
511‚Üí            lastValidationTime: Date.now(),
512‚Üí          }));
513‚Üí
514‚Üí          return true;
515‚Üí        }
516‚Üí      );
517‚Üí    } catch (error) {
518‚Üí      console.error("Error refreshing user data:", error);
519‚Üí      throw error; // Re-throw for the session handler to catch
520‚Üí    } finally {
521‚Üí      setIsRoleLoading(false);
522‚Üí    }
523‚Üí  }, [userId, getToken]);
524‚Üí
525‚Üí  const value: AuthContextType = {
526‚Üí    // Authentication state
527‚Üí    isAuthenticated: !!isSignedIn,
528‚Üí    isLoading: !isClerkLoaded || isRoleLoading,
529‚Üí    userId: userId || null,
530‚Üí    userEmail: user?.emailAddresses[0]?.emailAddress || null,
531‚Üí    userName: user?.fullName || user?.firstName || null,
532‚Üí
533‚Üí    // Role and permissions
534‚Üí    userRole,
535‚Üí    userPermissions: permissions,
536‚Üí    hasPermission,
537‚Üí    hasAnyPermission,
538‚Üí    hasRole,
539‚Üí
540‚Üí    // Admin access checks
541‚Üí    hasAdminAccess,
542‚Üí    canManageUsers,
543‚Üí    canManageClients,
544‚Üí    canProcessPayrolls,
545‚Üí    canViewFinancials,
546‚Üí
547‚Üí    // Actions
548‚Üí    signOut,
549‚Üí    refreshUserData,
550‚Üí  };
551‚Üí
552‚Üí  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
553‚Üí}
554‚Üí
555‚Üíexport function useAuthContext() {
556‚Üí  const context = useContext(AuthContext);
557‚Üí  if (context === undefined) {
558‚Üí    throw new Error("useAuthContext must be used within an AuthProvider");
559‚Üí  }
560‚Üí  return context;
561‚Üí}
562‚Üí
563‚Üí// Export permissions for use in other components
564‚Üíexport { PERMISSIONS };
565‚Üí

