  1‚Üí// hooks/usePolling.ts
  2‚Üíimport { useEffect, useRef } from 'react';
  3‚Üíimport { ApolloQueryResult, OperationVariables, QueryResult } from '@apollo/client';
  4‚Üí
  5‚Üí/**
  6‚Üí * Options for the useSmartPolling hook
  7‚Üí */
  8‚Üíinterface UseSmartPollingOptions {
  9‚Üí  /**
 10‚Üí   * Default polling interval in milliseconds
 11‚Üí   */
 12‚Üí  defaultInterval?: number;
 13‚Üí  
 14‚Üí  /**
 15‚Üí   * Whether to pause polling when the window is not visible
 16‚Üí   */
 17‚Üí  pauseOnHidden?: boolean;
 18‚Üí  
 19‚Üí  /**
 20‚Üí   * Whether to refetch immediately when the window becomes visible again
 21‚Üí   */
 22‚Üí  refetchOnVisible?: boolean;
 23‚Üí  
 24‚Üí  /**
 25‚Üí   * Whether to pause polling when the network is offline
 26‚Üí   */
 27‚Üí  pauseOnOffline?: boolean;
 28‚Üí  
 29‚Üí  /**
 30‚Üí   * Whether to refetch immediately when the network comes back online
 31‚Üí   */
 32‚Üí  refetchOnOnline?: boolean;
 33‚Üí}
 34‚Üí
 35‚Üí/**
 36‚Üí * A hook for smart data polling that adapts to window visibility and network status
 37‚Üí * 
 38‚Üí * @param queryResult The result from a useQuery hook
 39‚Üí * @param options Configuration options
 40‚Üí */
 41‚Üíexport function useSmartPolling<TData = any, TVariables extends OperationVariables = OperationVariables>(
 42‚Üí  queryResult: Pick<QueryResult<TData, TVariables>, 'startPolling' | 'stopPolling' | 'refetch'>,
 43‚Üí  options: UseSmartPollingOptions = {}
 44‚Üí) {
 45‚Üí  const {
 46‚Üí    defaultInterval = 30000,
 47‚Üí    pauseOnHidden = true,
 48‚Üí    refetchOnVisible = true,
 49‚Üí    pauseOnOffline = true,
 50‚Üí    refetchOnOnline = true
 51‚Üí  } = options;
 52‚Üí  
 53‚Üí  // Store current polling state to avoid unnecessary start/stop cycles
 54‚Üí  const pollingStateRef = useRef({
 55‚Üí    isPolling: true,
 56‚Üí    currentInterval: defaultInterval
 57‚Üí  });
 58‚Üí  
 59‚Üí  // Start polling initially
 60‚Üí  useEffect(() => {
 61‚Üí    queryResult.startPolling(defaultInterval);
 62‚Üí    
 63‚Üí    return () => {
 64‚Üí      queryResult.stopPolling();
 65‚Üí    };
 66‚Üí  }, [queryResult, defaultInterval]);
 67‚Üí  
 68‚Üí  // Handle window visibility changes
 69‚Üí  useEffect(() => {
 70‚Üí    if (!pauseOnHidden) return;
 71‚Üí    
 72‚Üí    const handleVisibilityChange = () => {
 73‚Üí      if (document.hidden) {
 74‚Üí        // Window is hidden, pause polling
 75‚Üí        if (pollingStateRef.current.isPolling) {
 76‚Üí          queryResult.stopPolling();
 77‚Üí          pollingStateRef.current.isPolling = false;
 78‚Üí        }
 79‚Üí      } else {
 80‚Üí        // Window is visible again
 81‚Üí        if (!pollingStateRef.current.isPolling) {
 82‚Üí          // Optionally do an immediate refetch
 83‚Üí          if (refetchOnVisible) {
 84‚Üí            queryResult.refetch();
 85‚Üí          }
 86‚Üí          
 87‚Üí          // Restart polling
 88‚Üí          queryResult.startPolling(pollingStateRef.current.currentInterval);
 89‚Üí          pollingStateRef.current.isPolling = true;
 90‚Üí        }
 91‚Üí      }
 92‚Üí    };
 93‚Üí    
 94‚Üí    document.addEventListener('visibilitychange', handleVisibilityChange);
 95‚Üí    
 96‚Üí    return () => {
 97‚Üí      document.removeEventListener('visibilitychange', handleVisibilityChange);
 98‚Üí    };
 99‚Üí  }, [queryResult, pauseOnHidden, refetchOnVisible]);
100‚Üí  
101‚Üí  // Handle network status changes
102‚Üí  useEffect(() => {
103‚Üí    if (!pauseOnOffline) return;
104‚Üí    
105‚Üí    const handleOnline = () => {
106‚Üí      if (!pollingStateRef.current.isPolling) {
107‚Üí        // Optionally do an immediate refetch
108‚Üí        if (refetchOnOnline) {
109‚Üí          queryResult.refetch();
110‚Üí        }
111‚Üí        
112‚Üí        // Restart polling
113‚Üí        queryResult.startPolling(pollingStateRef.current.currentInterval);
114‚Üí        pollingStateRef.current.isPolling = true;
115‚Üí      }
116‚Üí    };
117‚Üí    
118‚Üí    const handleOffline = () => {
119‚Üí      if (pollingStateRef.current.isPolling) {
120‚Üí        queryResult.stopPolling();
121‚Üí        pollingStateRef.current.isPolling = false;
122‚Üí      }
123‚Üí    };
124‚Üí    
125‚Üí    window.addEventListener('online', handleOnline);
126‚Üí    window.addEventListener('offline', handleOffline);
127‚Üí    
