  2‚Üí"use client";
  3‚Üí
  4‚Üíimport React, { useEffect, useState } from "react";
  5‚Üíimport { useUser } from "@clerk/nextjs";
  6‚Üíimport { useCurrentUser } from "@/hooks/useCurrentUser";
  7‚Üí
  8‚Üíinterface StrictDatabaseGuardProps {
  9‚Üí  children: React.ReactNode;
 10‚Üí}
 11‚Üí
 12‚Üí/**
 13‚Üí * STRICT Security Guard - Blocks ALL access until database user is verified
 14‚Üí * 
 15‚Üí * This component takes a zero-tolerance approach:
 16‚Üí * - If user exists in Clerk but NOT in database ‚Üí BLOCK ACCESS
 17‚Üí * - Only allows access when user exists in BOTH Clerk AND database
 18‚Üí */
 19‚Üíexport function StrictDatabaseGuard({ children }: StrictDatabaseGuardProps) {
 20‚Üí  const { user: clerkUser, isLoaded: clerkLoaded } = useUser();
 21‚Üí  const { currentUser, loading: dbUserLoading, error: dbUserError, isReady } = useCurrentUser();
 22‚Üí  const [isBlocked, setIsBlocked] = useState(false);
 23‚Üí  const [blockReason, setBlockReason] = useState<string>("");
 24‚Üí  const [gracePeriodEnded, setGracePeriodEnded] = useState(false);
 25‚Üí
 26‚Üí  // Grace period to allow authentication to settle
 27‚Üí  useEffect(() => {
 28‚Üí    if (clerkLoaded && clerkUser && !gracePeriodEnded) {
 29‚Üí      // Give authentication flow 3 seconds to settle
 30‚Üí      const timer = setTimeout(() => {
 31‚Üí        setGracePeriodEnded(true);
 32‚Üí      }, 3000);
 33‚Üí      
 34‚Üí      return () => clearTimeout(timer);
 35‚Üí    }
 36‚Üí  }, [clerkLoaded, clerkUser]);
 37‚Üí
 38‚Üí  // Monitor access status
 39‚Üí  useEffect(() => {
 40‚Üí    // Wait for Clerk to load
 41‚Üí    if (!clerkLoaded) {
 42‚Üí      return;
 43‚Üí    }
 44‚Üí
 45‚Üí    // No Clerk user - let normal auth flow handle this
 46‚Üí    if (!clerkUser) {
 47‚Üí      setIsBlocked(false);
 48‚Üí      setBlockReason("");
 49‚Üí      return;
 50‚Üí    }
 51‚Üí
 52‚Üí    // Still loading database check OR initial user ID extraction, or within grace period
 53‚Üí    if (dbUserLoading || !isReady || !gracePeriodEnded) {
 54‚Üí      return;
 55‚Üí    }
 56‚Üí
 57‚Üí    // Database error - block as precaution
 58‚Üí    if (dbUserError) {
 59‚Üí      setIsBlocked(true);
 60‚Üí      setBlockReason("Database connection error");
 61‚Üí      console.error("üö® BLOCKING ACCESS: Database error", dbUserError);
 62‚Üí      return;
 63‚Üí    }
 64‚Üí
 65‚Üí    // ‚úÖ All checks passed - user exists in both systems
 66‚Üí    if (clerkUser && currentUser) {
 67‚Üí      setIsBlocked(false);
 68‚Üí      setBlockReason("");
 69‚Üí      console.log("‚úÖ ACCESS GRANTED: User verified in both Clerk and database", {
 70‚Üí        clerkUserId: clerkUser.id,
 71‚Üí        databaseUserId: currentUser.id,
 72‚Üí        userEmail: currentUser.email
 73‚Üí      });
 74‚Üí      return;
 75‚Üí    }
 76‚Üí
 77‚Üí    // üö® CRITICAL: User in Clerk but NOT in database
 78‚Üí    // Only block if we're definitely done loading, ready, grace period ended, and still no user
 79‚Üí    if (clerkUser && !currentUser && !dbUserLoading && isReady && gracePeriodEnded) {
 80‚Üí      setIsBlocked(true);
 81‚Üí      setBlockReason("User not found in database");
 82‚Üí      console.error("üö® BLOCKING ACCESS: User authenticated with Clerk but not found in database", {
 83‚Üí        clerkUserId: clerkUser.id,
 84‚Üí        userEmail: clerkUser.emailAddresses?.[0]?.emailAddress,
 85‚Üí        timestamp: new Date().toISOString(),
 86‚Üí        isReady,
 87‚Üí        gracePeriodEnded,
 88‚Üí        dbUserLoading
 89‚Üí      });
 90‚Üí      
 91‚Üí      // Additional security logging
 92‚Üí      console.error("üõ°Ô∏è ACCESS DENIED - Security violation detected");
 93‚Üí      console.error("üìß User email:", clerkUser.emailAddresses?.[0]?.emailAddress);
 94‚Üí      console.error("üÜî Clerk ID:", clerkUser.id);
 95‚Üí      return;
 96‚Üí    }
 97‚Üí
 98‚Üí  }, [clerkLoaded, clerkUser, currentUser, dbUserLoading, dbUserError, isReady, gracePeriodEnded]);
 99‚Üí
100‚Üí  // Show loading while verifying
101‚Üí  if (!clerkLoaded || (clerkUser && (dbUserLoading || !isReady || !gracePeriodEnded))) {
102‚Üí    return (
103‚Üí      <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
104‚Üí        <div className="text-center max-w-md mx-auto p-8">
105‚Üí          <div className="relative mb-6">
106‚Üí            <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-200 border-t-blue-600 mx-auto"></div>
107‚Üí            <div className="absolute inset-0 flex items-center justify-center">
108‚Üí              <span className="text-lg">üõ°Ô∏è</span>
109‚Üí            </div>
110‚Üí          </div>
111‚Üí          
112‚Üí          <h2 className="text-xl font-semibold text-gray-800 mb-2">
113‚Üí            Verifying Access
114‚Üí          </h2>
115‚Üí          
116‚Üí          <p className="text-gray-600 mb-4">
117‚Üí            Checking your security credentials and permissions...
118‚Üí            {clerkUser && !gracePeriodEnded && (
119‚Üí              <><br /><span className="text-sm text-gray-500">Initializing secure connection...</span></>
120‚Üí            )}
121‚Üí          </p>
122‚Üí          
123‚Üí          <div className="flex items-center justify-center space-x-2 text-sm text-gray-500">
124‚Üí            <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
125‚Üí            <span>This should only take a moment</span>
126‚Üí          </div>
127‚Üí        </div>
128‚Üí      </div>
129‚Üí    );
130‚Üí  }
131‚Üí
132‚Üí  // üö® BLOCK ACCESS - Show security block screen
133‚Üí  if (isBlocked) {
134‚Üí    return (
135‚Üí      <div className="flex items-center justify-center min-h-screen bg-red-50">
136‚Üí        <div className="text-center max-w-md mx-auto p-6">
137‚Üí          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
