 72‚Üí
 73‚Üí      // Step 3: Force token refresh - we'll let the auth context handle this
 74‚Üí      console.log("üîë Token refresh will be handled by auth context...");
 75‚Üí
 76‚Üí      // Step 4: Refresh user data
 77‚Üí      console.log("üë§ Refreshing user data...");
 78‚Üí      try {
 79‚Üí        await refreshUserData();
 80‚Üí        console.log("‚úÖ User data refreshed");
 81‚Üí      } catch (refreshError) {
 82‚Üí        console.error("‚ùå Failed to refresh user data:", refreshError);
 83‚Üí        throw refreshError;
 84‚Üí      }
 85‚Üí
 86‚Üí      // Give a moment for state to update
 87‚Üí      await new Promise(resolve => setTimeout(resolve, 1000));
 88‚Üí      
 89‚Üí      const success = true;
 90‚Üí
 91‚Üí      if (success) {
