151‚Üí      if (
152‚Üí        errorMessage &&
153‚Üí        typeof errorMessage === "string" &&
154‚Üí        (errorMessage.includes("JWTExpired") ||
155‚Üí          errorMessage.includes("Could not verify JWT")) &&
156‚Üí        // Only handle if it's not from Apollo (Apollo has its own error handling)
157‚Üí        !errorMessage.includes("Apollo") &&
