162‚Üí      }
163‚Üí    };
164‚Üí
165‚Üí    // More specific handler for promise rejections
166‚Üí    const handleRejection = (event: PromiseRejectionEvent) => {
167‚Üí      const errorMessage = event.reason?.message || event.reason;
168‚Üí      
169‚Üí      // Be more specific and avoid interfering with Apollo
170‚Üí      if (
171‚Üí        errorMessage &&
172‚Üí        typeof errorMessage === "string" &&
173‚Üí        (errorMessage.includes("JWTExpired") ||
174‚Üí          errorMessage.includes("Could not verify JWT")) &&
175‚Üí        // Only handle if it's not from Apollo
176‚Üí        !errorMessage.includes("Apollo") &&
177‚Üí        !errorMessage.includes("GraphQL")
178‚Üí      ) {
179‚Üí        console.log("üö® Global JWT promise rejection detected:", errorMessage);
180‚Üí        handleSessionExpiry({ source: 'global_error', message: errorMessage });
181‚Üí      }
182‚Üí    };
183‚Üí
184‚Üí    // Add global error listeners only if enabled
185‚Üí    if (ENABLE_GLOBAL_ERROR_HANDLING) {
186‚Üí      window.addEventListener("error", handleError);
187‚Üí      window.addEventListener("unhandledrejection", handleRejection);
188‚Üí    }
189‚Üí
190‚Üí    // Add Apollo-specific session expired event listener
191‚Üí    window.addEventListener(
192‚Üí      SESSION_EXPIRED_EVENT,
