200‚Üí        processingEventRef.current = false;
201‚Üí        handlerIdRef.current = null;
202‚Üí      }
203‚Üí      
204‚Üí      // Only remove listeners if they were added
205‚Üí      if (ENABLE_GLOBAL_ERROR_HANDLING) {
206‚Üí        window.removeEventListener("error", handleError);
207‚Üí        window.removeEventListener("unhandledrejection", handleRejection);
208‚Üí      }
209‚Üí      window.removeEventListener(
210‚Üí        SESSION_EXPIRED_EVENT,
211‚Üí        handleSessionExpiredEvent as EventListener
212‚Üí      );
213‚Üí    };
214‚Üí  }, [handleSessionExpiry]);
215‚Üí
216‚Üí  // If session is expired and user hasn't clicked sign in, redirect after delay
217‚Üí  useEffect(() => {
218‚Üí    if (isSessionExpired) {
