224‚Üí
225‚Üí      return () => clearTimeout(redirectTimeout);
226‚Üí    }
227‚Üí  }, [isSessionExpired, signOut, router]);
228‚Üí
229‚Üí  // This component doesn't render anything visible
230‚Üí  return null;
231‚Üí}
232‚Üí
233‚Üíexport default SessionExpiryHandler;
