      }

      if (!response.ok) {
        // Handle dependency conflicts specifically
        if (response.status === 409 && result.dependencies) {
          const dependencyList = result.dependencies.join(", ");
          const suggestions = result.suggestions?.join("\n‚Ä¢ ") || "";

