  1‚Üí// lib/auth/auth-mutex.ts
  2‚Üí// Authentication mutex to prevent concurrent auth operations
  3‚Üí
  4‚Üíimport { useState, useEffect } from 'react';
  5‚Üí
  6‚Üíexport interface AuthOperation {
  7‚Üí  id: string;
  8‚Üí  type: 'user_extraction' | 'token_refresh' | 'session_validation' | 'permission_check';
  9‚Üí  timestamp: number;
 10‚Üí  promise?: Promise<any>;
 11‚Üí}
 12‚Üí
 13‚Üíexport interface MutexState {
 14‚Üí  isLocked: boolean;
 15‚Üí  currentOperation: AuthOperation | null;
 16‚Üí  queuedOperations: AuthOperation[];
 17‚Üí  lastOperationTime: number;
 18‚Üí}
 19‚Üí
 20‚Üíclass AuthMutex {
 21‚Üí  private static instance: AuthMutex;
 22‚Üí  private state: MutexState = {
 23‚Üí    isLocked: false,
 24‚Üí    currentOperation: null,
 25‚Üí    queuedOperations: [],
 26‚Üí    lastOperationTime: 0,
 27‚Üí  };
 28‚Üí  private lockTimeouts: Map<string, NodeJS.Timeout> = new Map();
 29‚Üí  private stateListeners: Set<(state: MutexState) => void> = new Set();
 30‚Üí
 31‚Üí  private constructor() {}
 32‚Üí
 33‚Üí  public static getInstance(): AuthMutex {
 34‚Üí    if (!AuthMutex.instance) {
 35‚Üí      AuthMutex.instance = new AuthMutex();
 36‚Üí    }
 37‚Üí    return AuthMutex.instance;
 38‚Üí  }
 39‚Üí
 40‚Üí  // Subscribe to mutex state changes
 41‚Üí  public subscribe(listener: (state: MutexState) => void): () => void {
 42‚Üí    this.stateListeners.add(listener);
 43‚Üí    return () => {
 44‚Üí      this.stateListeners.delete(listener);
 45‚Üí    };
 46‚Üí  }
 47‚Üí
 48‚Üí  // Get current mutex state (readonly)
 49‚Üí  public getState(): Readonly<MutexState> {
 50‚Üí    return {
 51‚Üí      ...this.state,
 52‚Üí      currentOperation: this.state.currentOperation ? { ...this.state.currentOperation } : null,
 53‚Üí      queuedOperations: [...this.state.queuedOperations],
 54‚Üí    };
 55‚Üí  }
 56‚Üí
 57‚Üí  // Acquire mutex lock for an operation
 58‚Üí  public async acquire<T>(
 59‚Üí    operationId: string,
 60‚Üí    operationType: AuthOperation['type'],
 61‚Üí    operation: () => Promise<T>,
 62‚Üí    timeout: number = 30000
 63‚Üí  ): Promise<T> {
 64‚Üí    console.log(`üîí Auth mutex acquire requested: ${operationId} (${operationType})`);
 65‚Üí
 66‚Üí    return new Promise<T>((resolve, reject) => {
 67‚Üí      const authOp: AuthOperation = {
 68‚Üí        id: operationId,
 69‚Üí        type: operationType,
 70‚Üí        timestamp: Date.now(),
 71‚Üí      };
 72‚Üí
 73‚Üí      // If not locked, acquire immediately
 74‚Üí      if (!this.state.isLocked) {
 75‚Üí        this.executeOperation(authOp, operation, resolve, reject, timeout);
 76‚Üí        return;
 77‚Üí      }
 78‚Üí
 79‚Üí      // If locked, queue the operation
 80‚Üí      console.log(`‚è≥ Auth mutex busy, queueing operation: ${operationId}`);
 81‚Üí      authOp.promise = new Promise<T>((opResolve, opReject) => {
 82‚Üí        // Set up timeout for queued operation
 83‚Üí        const timeoutId = setTimeout(() => {
 84‚Üí          this.removeFromQueue(operationId);
 85‚Üí          opReject(new Error(`Auth operation ${operationId} timed out after ${timeout}ms`));
 86‚Üí        }, timeout);
 87‚Üí
 88‚Üí        this.lockTimeouts.set(operationId, timeoutId);
 89‚Üí
 90‚Üí        // Store resolve/reject in operation for later use
 91‚Üí        (authOp as any).resolve = opResolve;
 92‚Üí        (authOp as any).reject = opReject;
 93‚Üí        (authOp as any).operation = operation;
 94‚Üí      });
 95‚Üí
 96‚Üí      this.state.queuedOperations.push(authOp);
 97‚Üí      this.notifyStateChange();
 98‚Üí
 99‚Üí      // Return the promise that will be resolved when operation runs
100‚Üí      authOp.promise.then(resolve).catch(reject);
101‚Üí    });
102‚Üí  }
103‚Üí
104‚Üí  // Execute an operation with the mutex lock
105‚Üí  private async executeOperation<T>(
106‚Üí    authOp: AuthOperation,
107‚Üí    operation: () => Promise<T>,
108‚Üí    resolve: (value: T) => void,
109‚Üí    reject: (error: Error) => void,
110‚Üí    timeout: number
111‚Üí  ): Promise<void> {
112‚Üí    // Acquire lock
113‚Üí    this.state.isLocked = true;
114‚Üí    this.state.currentOperation = authOp;
115‚Üí    this.state.lastOperationTime = Date.now();
116‚Üí    this.notifyStateChange();
117‚Üí
118‚Üí    console.log(`üîì Auth mutex acquired: ${authOp.id} (${authOp.type})`);
119‚Üí
120‚Üí    // Set up timeout
121‚Üí    const timeoutId = setTimeout(() => {
122‚Üí      console.error(`‚è∞ Auth operation timeout: ${authOp.id}`);
123‚Üí      this.release(authOp.id);
124‚Üí      reject(new Error(`Auth operation ${authOp.id} timed out after ${timeout}ms`));
125‚Üí    }, timeout);
126‚Üí
127‚Üí    this.lockTimeouts.set(authOp.id, timeoutId);
128‚Üí
129‚Üí    try {
130‚Üí      const result = await operation();
131‚Üí      
132‚Üí      // Clear timeout
133‚Üí      const timeoutRef = this.lockTimeouts.get(authOp.id);
134‚Üí      if (timeoutRef) {
135‚Üí        clearTimeout(timeoutRef);
136‚Üí        this.lockTimeouts.delete(authOp.id);
137‚Üí      }
138‚Üí
139‚Üí      console.log(`‚úÖ Auth operation completed: ${authOp.id}`);
140‚Üí      resolve(result);
141‚Üí    } catch (error) {
142‚Üí      console.error(`‚ùå Auth operation failed: ${authOp.id}`, error);
143‚Üí      
144‚Üí      // Clear timeout
145‚Üí      const timeoutRef = this.lockTimeouts.get(authOp.id);
146‚Üí      if (timeoutRef) {
147‚Üí        clearTimeout(timeoutRef);
148‚Üí        this.lockTimeouts.delete(authOp.id);
149‚Üí      }
150‚Üí
151‚Üí      reject(error instanceof Error ? error : new Error(String(error)));
152‚Üí    } finally {
153‚Üí      this.release(authOp.id);
154‚Üí    }
155‚Üí  }
156‚Üí
157‚Üí  // Release mutex lock
158‚Üí  public release(operationId: string): void {
159‚Üí    if (this.state.currentOperation?.id !== operationId) {
160‚Üí      console.warn(`‚ö†Ô∏è Auth mutex release ignored - not current operation: ${operationId}`);
161‚Üí      return;
162‚Üí    }
163‚Üí
164‚Üí    console.log(`üîì Auth mutex released: ${operationId}`);
165‚Üí
166‚Üí    // Clear current operation
167‚Üí    this.state.isLocked = false;
168‚Üí    this.state.currentOperation = null;
169‚Üí
170‚Üí    // Clean up timeout
171‚Üí    const timeoutRef = this.lockTimeouts.get(operationId);
172‚Üí    if (timeoutRef) {
173‚Üí      clearTimeout(timeoutRef);
174‚Üí      this.lockTimeouts.delete(operationId);
175‚Üí    }
176‚Üí
177‚Üí    // Process next operation in queue
178‚Üí    this.processQueue();
179‚Üí    this.notifyStateChange();
180‚Üí  }
181‚Üí
182‚Üí  // Process the next operation in the queue
183‚Üí  private processQueue(): void {
184‚Üí    if (this.state.queuedOperations.length === 0) {
185‚Üí      return;
186‚Üí    }
187‚Üí
188‚Üí    const nextOp = this.state.queuedOperations.shift()!;
189‚Üí    console.log(`üîÑ Processing queued auth operation: ${nextOp.id}`);
190‚Üí
191‚Üí    // Get stored operation details
192‚Üí    const resolve = (nextOp as any).resolve;
193‚Üí    const reject = (nextOp as any).reject;
194‚Üí    const operation = (nextOp as any).operation;
195‚Üí
